/*-------------------------------------------------------------------------------------------------+
>>  메뉴상세 조회
+-------------------------------------------------------------------------------------------------*/
function fcGetMenuDetail(sIndex) {
    if(length(dsMenuListValue.getColumn(sIndex, "MN_ID"	    )) > 0 ) {
        edMenuId.text       = dsMenuListValue.getColumn(sIndex, "MN_ID"	    );
        edMenuId.readonly   = true;
        edMenuName.text     = dsMenuListValue.getColumn(sIndex, "MN_NM"	    );
        edUpMenu.text       = dsMenuGrpValue.getColumn(grdMenuGrp.ClickedRow, "MN_ID"	);
//      edUpMenu.readonly   = true;
        edUpMnNm.text       = dsMenuGrpValue.getColumn(grdMenuGrp.ClickedRow, "MN_NM"   );
//      edUpMnNm.readonly   = true;
        edUrl.text          = dsMenuListValue.getColumn(sIndex, "URL"		);
        taRmk.text          = dsMenuListValue.getColumn(sIndex, "RMK"		);
    } else {
        edMenuId.readonly   = false;
    }
}


/*-------------------------------------------------------------------------------------------------+
>>  메뉴리스트 조회 선작업
+-------------------------------------------------------------------------------------------------*/
function fcGetMenuListBefore(obj,nOldRow,nRow) {        
    if(!fnc_isNull(obj.getColumn(nRow, "MN_ID"))) {
        fcGetMenuList(dsMenuGrpValue.getColumn(nRow, "MN_ID"));
    }
}


/*-------------------------------------------------------------------------------------------------+
>>  메뉴상세 조회 선작업
+-------------------------------------------------------------------------------------------------*/
function fcGetMenuDetailBefore(obj) {
    var clickedRow = obj.GetClickedRow();
    fcGetMenuDetail(clickedRow);   
}



