function dsMenuGrpValue_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "MN_ID" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "메뉴그룹ID가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "메뉴그룹ID가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	}
}

function dsMenuListValue_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "MN_ID" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "메뉴ID가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "메뉴ID가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	}	
}

function dsMenuListValue_OnRowPosChanged(obj,nOldRow,nRow)
{
 /*   
    if ( dsMenuListValue.GetRowCount() == 0 ) return;

    if ( dsMenuListValue.GetRowCount() <= dsMenuListValue.currow ) {
        dsMenuListValue.FireEvent = false;
        dsMenuListValue.row = dsMenuListValue.GetRowCount() - 1;
        dsMenuListValue.SelectRow(dsMenuListValue.GetRowCount() - 1);
        dsMenuListValue.FireEvent = true;
    }

	if ( fnc_DatasetChangeCheck(dsMenuListValue) ) {
		if ( fnc_Message(SNIS_COM_1002, "Y") ) {
            dsMenuListValue.FireEvent = false;
            dsMenuListValue.row = nOldRow;
			fcSave();
            dsMenuListValue.FireEvent = true;
			return;
		}
	}

    fcInit();
    if ( fnc_IsNull(dsMenuListValue.GetColumn(nRow, "MN_ID")) ) return;

	//fcSearchDetail();
    */ 

    if ( dsMenuListValue.GetRowType(nRow) == "insert" ) {
        edMenuId.Readonly = false;
	} else {
        edMenuId.Readonly = true;
	}

	fnc_SetBKColor(this);   
}

