/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    if(sInpt == "F"){
		btnCfm.Visible 	  = false;
		btnCfmCls.Visible = false; 
    }

    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {
        dsOutWkStandard.ClearData();
    }    
}
/*-------------------------------------------------------------------------------------------------+
>>  cfm config
+-------------------------------------------------------------------------------------------------*/

function fcCfm() {

    var sServiceName  = "d06000001-service:searchCfm";
    var sInDataSet    = "";
    var sOutDataSet   = " dsCfm=dsCfm ";

	fcd_AddParam(Trim("racerPerioNo"), cbPerioNo.Value);
	fcd_AddParam(Trim("yy"), "");
	fcd_AddParam(Trim("mn_id"), sFormId);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {

	fcInit(obj);

	if(fnc_IsNull(cbPerioNo.Value)) {
        fnc_Message(SNIS_CAN_0008, "N", cbPerioNo.Value);
        return false;
    }

	return true;
}

function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "d06000001-service:searchWkStandard";
    var sInDataSet   = "";
    var sOutDataSet  = " dsOutWkStandard=dsOutWkStandard ";

	fcd_AddParam(Trim("racerPerioNo"), Trim(cbPerioNo.Value));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {

    if((dsCfm.GetColumn(0,"CFM_FG")) == "Y"){
		fnc_Message(SNIS_CAN_0012, "N", "주단위기준");
		return false;
    }

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutWkStandard) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	oDataSet = null;
	sMandatoryColID   = null;
	sMandatoryColName = null;
	oGrid             = null;

	oDataSet = dsOutWkStandard;
	sMandatoryColID   = "WK|STR_DT|END_DT";
	sMandatoryColName = "주차|시작일자|종료일자";
	oGrid             = grdWkStandard;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	// 주차 중복
	if( fnc_IsColumnDuplicate(dsOutWkStandard, "WK") ) {

        fnc_Message(SNIS_CAN_0009, "N", "주차");
        return false;
	}

	// 시작일자 중복 체크
	if( fnc_IsColumnDuplicate(dsOutWkStandard, "STR_DT") ) {

        fnc_Message(SNIS_CAN_0009, "N", "시작일자");
        return false;
	}

	// 종료일자 중복 체크 
	if( fnc_IsColumnDuplicate(dsOutWkStandard, "END_DT") ) {

        fnc_Message(SNIS_CAN_0009, "N", "종료일자");
        return false;
	}

	// 기수 입력항이 한자리일 경우
	if( Bound(meInPerioNo.Value) == 1 ) {

       meInPerioNo.Value = Trim("0" + meInPerioNo.Value);
	}

	// 년도 입력항이 4자리가 아닐 경우
	if( Bound(meInYear.Value) != 4 ) {

     //  fnc_Message(SNIS_CAN_C001, "N", "년도");
     //  return false;
	}

	// 월이 선택되지 않았을 경우
	if( (cbInMonth.Value == null) || (cbInMonth.Value == "") ) {
      //  fnc_Message(SNIS_CAN_c001, "N", "월");
	}

	return true;
}

// 저장 공통
function fcSave(obj) {
   if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    sServiceName  = "d06000001-service:saveWkStandard";

    sInDataSet     = " dsOutWkStandard=dsOutWkStandard:u " ;
    sOutDataSet    = " dsOutWkStandard=dsOutWkStandard " ;

   for(var i=0; i<dsOutWkStandard.rowcount; i++) {   
		if(dsOutWkStandard.GetRowType(i) == "insert")
		{
			dsOutWkStandard.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value)) ;
			dsOutWkStandard.SetColumn(i, "YY", SubStr(dsOutWkStandard.GetColumn(i,"STR_DT"), 0, 4)) ;
			dsOutWkStandard.SetColumn(i, "MM", "0" + SubStr(dsOutWkStandard.GetColumn(i,"STR_DT"), 4, 2)) ;
		}
    }

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


