/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    switch(sServiceName) {

        // 공통코드 조회시
        case "common-service:searchCode" :
            dsMonth.InsertRow(0); 
            dsMonth.SetColumn(0, "CD_NM", '전체'); 
            break;         

        // 기수코드 조회후 콜백
        case "d06000001-service:searchPerioNo" :
            dsYear.InsertRow(0);
		    dsYear.SetColumn(0, "YY_NM", '전체'); 
            btnCfm.Enable = false;
            btnCfmCls.Enable = false; 
            fcSearch(); 
            break;

        // 검색조건으로 조회후 콜백
        case "d06000001-service:searchWkStandard" :

            fcCfm();
            break;

        case "d06000001-service:searchCfm" :
            if(dsCfm.RowCount() == 0){ 
				fcCfmInsert();
            }
            if((dsCfm.GetColumn(0,"CFM_FG")) == "Y"){
                btnCfm.Enable = false;
            	btnCfmCls.Enable = true;
            	divBtnLst.btnSave.Enable = false;
            	grdWkStandard.Editable = false;
            }else{
            	btnCfm.Enable = true;
            	btnCfmCls.Enable = false;
            	divBtnLst.btnSave.Enable = true;
            	grdWkStandard.Editable = true;
            }
            break;

        case "d06000001-service:saveCfm" :
			fcSearch();
			break;

        // 주단위 기준 저장후 콜백   
        case "d06000001-service:saveWkStandard" :  

            fcSetCombo();
            break;

        default :
            break;
    }

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리
	if ( obj.GetForm().id == "divBtnRow" ) {
       dsOutWkStandard.AddRow();
       dsOutWkStandard.SetColumn(dsOutWkStandard.lastrow, "GRIDSTAT", "1");
       dsOutWkStandard.SetColumn(dsOutWkStandard.lastrow, "WK",  dsOutWkStandard.RowCount() );
   }        
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRow" ) {
            var sArg = "";
			if ( !fnc_DeleteCheck(dsOutWkStandard, "chk", "1") ) return;
	} 
}

/*-------------------------------------------------------------------------------------------------+
>>  각 그리드의 레코드 상태 표시 초기화(편집 이전)
+-------------------------------------------------------------------------------------------------*/
function fcSetGridStat() {
    dsOutWkStandard.AddColumn("GRIDSTAT");
        for(var i=0; i<dsOutWkStandard.rowcount; i++) {
            dsOutWkStandard.SetColumn(i, "GRIDSTAT", "0");
    }
}
/*-------------------------------------------------------------------------------------------------+
>> 
+-------------------------------------------------------------------------------------------------*/

function fcCfmInsert(){

	dsCfm.ClearData();
	dsCfm.AddRow();	
	dsCfm.SetColumn(0,"RACER_PERIO_NO",cbPerioNo.Value);
	dsCfm.SetColumn(0,"YY",cbYear.Value);
	dsCfm.SetColumn(0,"MN_ID",sFormId );
}

/*-------------------------------------------------------------------------------------------------+
>>  콤보  설정
+-------------------------------------------------------------------------------------------------*/

function fcSetCombo() {

    var sServiceName  = "d06000001-service:searchPerioNo";
    var sInDataSet    = "";
    var sOutDataSet   = " dsPerioNo=dsPerioNo ";
        sOutDataSet  += " dsYear=dsYear ";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);        
}


// 그리드 레코드 변경시 이벤트

function dsOutWkStandard_OnRowPosChanged(obj,nOldRow,nRow)
{

    if(grdWkStandard.ClickedCell == 0) {

        var sArg = "";
        if ( dsOutWkStandard.GetRowCount() == 0 ) return;

        if ( dsOutWkStandard.GetRowCount() <= dsOutWkStandard.currow ) {
            dsOutWkStandard.FireEvent = false;
            dsOutWkStandard.row = dsOutWkStandard.GetRowCount() - 1;
            dsOutWkStandard.SelectRow(dsOutWkStandard.GetRowCount() - 1);
            dsOutWkStandard.FireEvent = true;
        }

        if ( fnc_DatasetChangeCheck(dsOutWkStandard) ) {
            if ( fnc_Message(SNIS_COM_1002, "Y") ) {
                dsOutWkStandard.FireEvent = false;
                dsOutWkStandard.row = nOldRow;
                fcSave();
                dsOutWkStandard.FireEvent = true;
                return;
            }
        }
   	}
}

// 주차 변경 방지 처리
function dsOutWkStandard_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(dsOutWkStandard.GetColumn(nRow, "GRIDSTAT") == "0") {
        if(strColumnID == "WK") {
            fnc_Message(SNIS_CAN_M003, "N", "주차" );
            dsOutWkStandard.FireEvent = false;     
            dsOutWkStandard.SetColumn(nRow, "WK", varOldValue);
            var val = null;
            for(var i=0; i<dsWeek.rowcount; i++) {
                if(dsWeek.GetColumn(i,"CD") == varOldValue) {
                    val = dsWeek.GetColumn(i,"CD_NM"); 
                }
            }
            grdWkStandard.SetCellProp("body", dsOutWkStandard.GetColIndex("WK"),text,val);
            dsOutWkStandard.FireEvent = true;
        }
	}
}

// 기수 자료 최초 로딩일경우 
function dsPerioNo_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if(dsPerioNo.rowcount == 0) {
        dsPerioNo.InsertRow(0);
        dsPerioNo.SetColumn(0, 0, "최초입력");
	}
}

// 해당 기수 자료 년도 최초 로딩일경우
function dsYear_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if(dsYear.rowcount == 0) {
        dsYear.InsertRow(0);
        dsYear.SetColumn(0, 0, "최초입력");
	}
}


