function fcInit(obj) {
	dsOutEduOutl.ClearData();
	dsOutEduAssign.ClearData();
	dsDetlEduTime.ClearData();
	dsDetlEduAssign.ClearData();
	dsOutEduPersCnt.ClearData();
	dsOutYearEduSch.ClearData();
}


/*-------------------------------------------------------------------------------------------------+
>>  세부교육일정 확정여부조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchDetlCfmYn(obj) {

	var sServiceName = "d06000002-service:searchDetlCfmYn";
    var sInDataSet   = "";
    var sOutDataSet  = " dsDetlCfmYn=dsDetlCfmYn ";

	dsDetlCfmYn.clear();
	fcd_AddParam("racerPerioNo", Trim(cbRacerPeriodNo.Value));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	if((fnc_IsNull(cbRacerPeriodNo.Value)) || fnc_IsNull(cbRacerPeriodNo.value)) {
        //조회할 자료가 없음
		fnc_Message(SNIS_CAN_0008, "N");
		return false;
	}

	if(dsDetlCfmYn.GetColumn(0,"CFM_YN") != "Y") {
		fnc_Message("세부교육일정이 확정되지 않았습니다.", "N");
		return false;
	}
	return true;
}

function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "d06000002-service:searchEduTotal";
    var sInDataSet   = "";
    var sOutDataSet  = " dsOutEduOutl=dsOutEduOutl ";
        sOutDataSet += " dsOutEduAssign=dsOutEduAssign ";
        sOutDataSet += " dsDetlEduTime=dsDetlEduTime ";
        sOutDataSet += " dsDetlEduAssign=dsDetlEduAssign ";
        sOutDataSet += " dsOutEduPersCnt=dsOutEduPersCnt ";
        sOutDataSet += " dsOutYearEduSch=dsOutYearEduSch ";

	fcd_AddParam("racerPerioNo", Trim(cbRacerPeriodNo.Value));
	fcd_AddParam("newGbnCd", Trim(cbCrsGbn.Value));		
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


