/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    switch(sServiceName) {
        // 공통코드 조회시
        case "common-service:searchCode" :

			dsGbn.InsertRow(0); 
			dsGbn.SetColumn(0, "CD_NM", "전체"); 
			cbCrsGbn.Index = 0;
			fcSearchDetlCfmYn();
			//fcSearch();
	        break;
        //  세부교육일정 확정여부조회
        case "d06000002-service:searchDetlCfmYn" :
			//fcSearchDetlCfmYn();
			fcSearch();
            break;

        //  기수조회 트랜젝션후 콜백 처리    
        case "d06000002-service:searchPerioNo" :
            break;

        // 검색조건으로 조회했을 경우
        case "d06000002-service:searchEduTotal" :
            edStrDt.Text = calEduStartNoRacer.Text;
            edEndDt.Text = calEduEndNoRacer.Text;
            grdYearEduSch.HeadBorder = "None";
            grdYearEduSch.SetCellProp("Head",0,"Text","제 "+cbRacerPeriodNo.value+"기 경정선수후보생 연간 교육계획 일정표");
			grdYearEduSch.SetCellProp("Head",1,"Text","교육기간: " + edStrDt.value+" ~ "+edEndDt.value +"("+dsOutEduOutl.GetColumn(0,"TOT_DAY_CNT")+"일간 실교육일수" +dsOutEduOutl.GetColumn(0,"EDU_DAY_CNT")+"일)");
			grdYearEduSch.SetCellProp("Head",1,"Align","left");

            fcSetGridStat();         
            break;

        case "d06000002-service:saveEduOutl" :  
            break;

        default :
            break;
    }

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
