/*-------------------------------------------------------------------------------------------------+
>>  각 그리드의 레코드 상태 표시 초기화(편집 이전)
+-------------------------------------------------------------------------------------------------*/
function fcSetGridStat() {
    dsOutEduAssign.AddColumn("GRIDSTAT");
        for(var i=0; i<dsOutEduAssign.RowCount(); i++) {
            dsOutEduAssign.SetColumn(i, "GRIDSTAT", "0");
    }

   if ( dsOutEduOutl.RowCount() == "0" )  {
       dsOutEduOutl.ClearData();
	   dsOutEduOutl.AddRow();
       dsOutEduOutl.SetColumn("0", "RACER_PERIO_NO", cbRacerPeriodNo.Value);
   }

}

/*-------------------------------------------------------------------------------------------------+
>>  기수콤보  설정
+-------------------------------------------------------------------------------------------------*/

function fcSetCombo() {

    var sServiceName  = "d06000002-service:searchPerioNo";
    var sInDataSet    = "";
    var sOutDataSet   = " dsPerioNo=dsPerioNo ";
        sOutDataSet  += " dsGudLectr=dsGudLectr ";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);        
}

// 구분에 따른 과목 필터링
function grdEduTime_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	if (nCell == 2){
		if (fnc_IsNull(dsOutEduAssign.GetColumn(nRow,"SECTN_CD"))){
			fnc_Message(SNIS_CAN_0008, "N", "구분");
			grdEduTime.SetCellPos(1);
		    return false;
		}
	}
}

// 구분, 과목에 따른 과목 필터링
function dsOutEduAssign_OnRowPosChanged(obj,nOldRow,nRow)
{
	var sectnCd = dsOutEduAssign.GetColumn(nRow,"SECTN_CD");

		switch(sectnCd){
			case "001" : dsCrsGrid.Filter("GRP_CD='D10030'"); 	break;		//학과
			case "002" : dsCrsGrid.Filter("GRP_CD='D10064'");	break;		//실기	
			case "003" : dsCrsGrid.Filter("GRP_CD='D10010'");	break;      //기타
		}
}


// 구분, 과목에 따른 과목 필터링
function dsOutEduAssign_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

    if (strColumnID == "SECTN_CD"){	 
		var sectnCd = dsOutEduAssign.GetColumn(nRow,"SECTN_CD");

		switch(sectnCd){
			case "001" : dsCrsGrid.Filter("GRP_CD='D10030'"); 	break;		//학과
			case "002" : dsCrsGrid.Filter("GRP_CD='D10064'");	break;		//실기	
			case "003" : dsCrsGrid.Filter("GRP_CD='D10010'");	break;      //기타
		}
	}


}

//기수
function cbRacerPeriodNo_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
	fcSearchDetlCfmYn();
}
//과목
function cbCrsGbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}
