>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {
//    edSCfrntCd.Text = Trim(edSCfrntCd.Text);
	fcInit(obj);

	return true;
}

function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "d06000003-service:searchEduDetail";
    var sInDataSet   = "";
    var sOutDataSet  = " dsDetlEduAssign=dsDetlEduAssign ";

	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));
	fcd_AddParam("yy", Trim(cbYear.Value));
	fcd_AddParam("mm", Trim(cbMonth.Value));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);

}

