function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리
	if ( obj.GetForm().id == "divBtnRow" ) {

        dsDetlEduAssign.AddRow();
           // dsEduAssign.SetColumn(0, SECTN_CD_NAME, dsEduAssignGbn.get);
    }        
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRow" ) {
            var sArg = "";

			if ( !fnc_DeleteCheck(dsDetlEduAssign, "chk", "1") ) return;
            fcInit();
			grdEduTime.SetFocus();           //Record 삭제 후 Grid로 Focus
			dsEduAssign_OnRowPosChanged();
			break;  	
	} 
}


function dsEduAssign_OnRowPosChanged(obj,nOldRow,nRow)
{
    if ( dsEduAssign.GetRowCount() == 0 ) return;

    if ( dsEduAssign.GetRowCount() <= dsEduAssign.currow ) {
        dsEduAssign.FireEvent = false;
        dsEduAssign.row = dsEduAssign.GetRowCount() - 1;
        dsEduAssign.SelectRow(dsEduAssign.GetRowCount() - 1);
        dsEduAssign.FireEvent = true;
    }

    fcInit();
    if ( fnc_IsNull(dsEduAssign.GetColumn(nRow, "SECTN_CD_NM")) ) return;
}


function fcCfmInsert(){

	dsCfm.ClearData();
	dsCfm.AddRow();	
	dsCfm.SetColumn(0,"RACER_PERIO_NO",cbPerioNo.Value);
	//dsCfm.SetColumn(0,"YY",cbYear.Value);
	dsCfm.SetColumn(0,"MN_ID",sFormId );
}
function fcCfm() {

    var sServiceName  = "d06000003-service:searchCfm";
    var sInDataSet    = "";
    var sOutDataSet   = " dsCfm=dsCfm ";

	fcd_AddParam(Trim("racerPerioNo"), cbPerioNo.Value);
	fcd_AddParam(Trim("yy"), cbYear.Value);
	fcd_AddParam(Trim("mn_id"), sFormId);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}
