// 구분에 따른 과목 필터링
function grdDetlEduAssign_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	 if (nCell == 4){
			if (fnc_IsNull(dsDetlEduAssign.GetColumn(nRow,"SECTN_CD"))){
				fnc_Message(SNIS_CAN_0008, "N", "구분");
				grdDetlEduAssign.SetCellPos(3);
				return false;
			}
	}

}

// 구분, 과목에 따른 과목 필터링 
function dsDetlEduAssign_OnRowPosChanged(obj,nOldRow,nRow)
{
	var sectnCd = dsDetlEduAssign.GetColumn(nRow,"SECTN_CD");

		switch(sectnCd){
			case "001" : dsCrs.Filter("GRP_CD='D10030'");   break;	//학과	
			case "002" : dsCrs.Filter("GRP_CD='D10064'");	break;	//실기		
			case "003" : dsCrs.Filter("GRP_CD='D10010'");	break;  //측정
		}
}

// 구분, 과목에 따른 과목 필터링
function dsDetlEduAssign_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "SECTN_CD"){	 
		var sectnCd = dsDetlEduAssign.GetColumn(nRow,"SECTN_CD");

		switch(sectnCd){
			case "001" : dsCrs.Filter("GRP_CD='D10030'");   break;	//학과	
			case "002" : dsCrs.Filter("GRP_CD='D10064'");	break;	//실기		
			case "003" : dsCrs.Filter("GRP_CD='D10010'");	break;  //측정
		}
	}

}

