function fcInit(obj) {

    if(sInpt == "F"){
		btn_Import.Visible = false; 
    }

    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {

        dsWkSche.ClearData();
        fcd_GetComboDs(dsInComCd);
    }    
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {

	fcInit(obj);

	//조회조건 누락
	if( fnc_IsNull(cbPerioNo.Value) ) {
        fnc_Message(SNIS_CAN_0008, "N", "기수");
		return false;
	} 


    return true;
}

function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "d06000004-service:searchWkSche";
    var sInDataSet   = "";
    var sOutDataSet  = " dsWkSche=dsWkSche ";
        sOutDataSet  += " dsWkPart=dsWkPart ";
        sOutDataSet  += " dsMMSche=dsMMSche ";

    fcd_AddParam("rPerioNo",  cbPerioNo.Value);
   	fcd_AddParam("wKweekPart", cbWeekPart.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function fcImport(obj)
{
	if (dsWkSche.rowcount > 0){
		fnc_Message("이미 주간 교육 일정이 있으므로 복사할 수 없습니다.", "N", "");
		return;
	}

    if ( !fcSearchCheck(obj) ) return;

	if (fnc_isNull(cbWeekPart.Value)) {		
		if (!fnc_message("전체 자료를 복사하시겠습니까?","Y")) return;
	}

    dsWkSche.ClearData();
    dsWkScheImport.ClearData();
    var sServiceName = "d06000004-service:importWkSche";
    var sInDataSet   = "";
    var sOutDataSet  = " dsWkScheImport=dsWkScheImport ";

    fcd_AddParam("rPerioNo", cbPerioNo.Value);
	fcd_AddParam("wKweekPart", cbWeekPart.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


