function fcSaveSet() {
    // 기수 자리수 조정
    if(Bound(cbPerioNo.Value) == 1) {

        cbPerioNo.Value = "0" + Trim(cbPerioNo.Value);
    }
}
// 저장 공통
function fcSave(obj) {

    if ( !fcSaveCheck() ) return;   
    fcSaveSet();

    // 기수, 년도, 월, 주차 채우기
    for(var i=0; i<dsWkSche.rowcount; i++) {

        dsWkSche.SetColumn(i,"RACER_PERIO_NO", cbPerioNo.Value);
        //dsWkSche.SetColumn(i,"WK",  cbWeekPart.Value);			//신재선 막음(조회시 처리하므로 추가 세팅할 필요 없음(2014.1.17)
        dsWkSche.SetColumn(i,"SEQ", dsWkSche.GetColumn(i,"SEQ"));
    }

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    sServiceName  = "d06000004-service:saveEduWeek";

    sInDataSet     = " dsWkSche=dsWkSche:u " ;
    sOutDataSet    = " dsWkSche=dsWkSche " ;


    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet,"fcCallBack");
}


