/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    switch(sServiceName) {

         // 공통코드 조회시
        case "common-service:searchCode" :

	        break;
        //  기수조회 트랜젝션후 콜백 처리    
        case "d06000004-service:searchNoYear" :    
            cbPerioNo.Index =0;

			fcSetWeek();

            break;

        case "d06000004-service:searchWK" :
			dsWeek.InsertRow(0); 
            dsWeek.SetColumn(0, "CD_NM", "전체");     
            cbWeekPart.Index = 0;
            grdWkSche.Editable = false;
            fcSearch();

			break;
        // 주차구간을 조회했을 경우
        case "d06000004-service:searchWkPart" :
            fcSearchCurEduStat();                                              
            break;

        // 교육개요 조회했을 경우 
        case "d06000004-service:searchEduTotal" :
			fcWkPartSet();
            break;        

        //  주간교육일정을 조회했을 경우 
        case "d06000004-service:searchWkSche":
			  if(dsWkSche.rowcount > 0)
			  {
				btn_Import.Enable = false;
			  }
			  else
			  {
				btn_Import.Enable = true;
			  }
              fcWkPartChange();                     
            break;

        case "d06000004-service:saveEduWeek" :
            btn_Import.Enable = false;   
            fcSearch();
            break;

        case "d06000004-service:searchPrint"  :
			fcPrintDetail();
            break;

        case "d06000004-service:importWkSche" :
			if(dsWkScheImport.rowcount > 0)
			{
				grdWkSche.Redraw = false;
				for(var i = 0; i < dsWkScheImport.rowcount; i++)
				{
					dsWkSche.AddRow();
					dsWkSche.setColumn(i,"RACER_PERIO_NO",dsWkScheImport.getColumn(i,"RACER_PERIO_NO"));
					dsWkSche.setColumn(i,"YY",dsWkScheImport.getColumn(i,"YY"));
					dsWkSche.setColumn(i,"MM",dsWkScheImport.getColumn(i,"MM"));
					dsWkSche.setColumn(i,"WK",dsWkScheImport.getColumn(i,"WK"));
					dsWkSche.setColumn(i,"DT",dsWkScheImport.getColumn(i,"DT"));
					//dsWkSche.setColumn(i,"STR_END_TIME",dsWkScheImport.getColumn(i,"STR_END_TIME"));
					dsWkSche.setColumn(i,"SECTN_CD",dsWkScheImport.getColumn(i,"SECTN_CD"));
					dsWkSche.setColumn(i,"CRS_CD",dsWkScheImport.getColumn(i,"CRS_CD"));
					dsWkSche.setColumn(i,"DETL_EDU_CRS",dsWkScheImport.getColumn(i,"DETL_EDU_CRS"));
					dsWkSche.setColumn(i,"OBJ",dsWkScheImport.getColumn(i,"OBJ"));
					dsWkSche.setColumn(i,"LECTR",dsWkScheImport.getColumn(i,"LECTR"));
					dsWkSche.setColumn(i,"PLC",dsWkScheImport.getColumn(i,"PLC"));
					dsWkSche.setColumn(i,"TIME",dsWkScheImport.getColumn(i,"TIME"));
					dsWkSche.setColumn(i,"STR_DT",dsWkScheImport.getColumn(i,"STR_DT"));
					dsWkSche.setColumn(i,"END_DT",dsWkScheImport.getColumn(i,"END_DT"));
				}
				grdWkSche.Redraw = true;
			}

			break;

		case "d06000004-service:searchMMSche" :
			fnc_OpenExcelExport("grdMMSche");
            break;

        default :
            break;
    }

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

