}

// 주차 콤보의 선택값이 변경될 경우
function fcWkPartChange() {

    var sServiceName = "d06000004-service:searchWkPart";
    var sInDataSet   = "";
    var sOutDataSet  = " dsWkPart=dsWkPart ";

    fcd_AddParam("perioNo", cbPerioNo.Value);
	fcd_AddParam("weekPart", cbWeekPart.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

// 주차구간의 일자 콤보 셋팅
function fcWkPartSet() {
    if(dsWkPart.RowCount() > 0) {
       dsWkDayList.ClearData();                              
       meStart.Text = dsWkPart.GetColumn(0, "STR_DT");
       meEnd.Text   = dsWkPart.GetColumn(dsWkPart.rowcount-1, "END_DT");

    }else{
		if(cbWeekPart.Value != null){	
			fnc_Message(SNIS_CAN_M002, "Y");
		}
		meStart.Text = "";
        meEnd.Text   = "";
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리
	if ( obj.GetForm().id == "divBtnRow" ) {
        if(cbWeekPart.Value == null){
			fnc_Message("주차가 전체 조회 일때는 추가할 수 없습니다.", "N", "");
			return;
		}

        dsWkSche.AddRow();
        var Cnt = dsWkSche.RowCount()-1;
        dsWkSche.SetColumn(Cnt,"DT",dsWkPart.GetColumn(0, "STR_DT"));
        dsWkSche.SetColumn(Cnt,"COPY","0");
    }        
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRow" ) {
		if(cbWeekPart.Value == null){
			fnc_Message("주차가 전체 조회 일때는 삭제할 수 없습니다.", "N", "");
			return;
		}
        var sArg = "";
        if ( !fnc_DeleteCheck(dsWkSche, "chk", "1") ) return;
	} 
}

