// 구분, 과목에 따른 과목 필터링 
function grdWkSche_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	 if (nCell == 4){
		if (fnc_IsNull(dsWkSche.GetColumn(nRow,"SECTN_CD"))){
			fnc_Message(SNIS_CAN_0008, "N", "구분");
			grdWkSche.SetCellPos(3);
		    return false;
		}
	}

}

// 구분, 과목에 따른 과목 필터링 
function dsWkSche_OnRowPosChanged(obj,nOldRow,nRow)
{
	var sectnCd = dsWkSche.GetColumn(nRow,"SECTN_CD");

		switch(sectnCd){
			case "001" : dsCrs.Filter("GRP_CD='D10030'"); 	break;	//학과	
			case "002" : dsCrs.Filter("GRP_CD='D10064'");	break;	//실기		
			case "003" : dsCrs.Filter("GRP_CD='D10010'");	break;  //측정
		}
}
/*-------------------------------------------------------------------------------------------------+
>>  구분, 과목에 따른 과목 필터링 및 시작및 종료시간에 대한 교육시간 자동계산
+-------------------------------------------------------------------------------------------------*/

function dsWkSche_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	if (strColumnID == "SECTN_CD"){	 
		var sectnCd = dsWkSche.GetColumn(nRow,"SECTN_CD");

		switch(sectnCd){
			case "001" : dsCrs.Filter("GRP_CD='D10030'"); 	break;	//학과	
			case "002" : dsCrs.Filter("GRP_CD='D10064'");	break;	//실기		
			case "003" : dsCrs.Filter("GRP_CD='D10010'");	break;  //측정
		}
	}

	if (strColumnID == "DT"){
		if ((dsWkPart.GetColumn(0, "STR_DT") > dsWkSche.GetColumn(nRow,"DT")) || (dsWkPart.GetColumn(dsWkPart.rowcount-1, "END_DT") < dsWkSche.GetColumn(nRow,"DT"))){
			 fnc_Message(SNIS_CAN_J004, "N");
			 dsWkSche.SetColumn(nRow,"DT",dsWkPart.GetColumn(0, "STR_DT"));
			}
	}

	if( strColumnID == "STR_END_TIME" ) {

        if( IsDigit(varNewValue) && (Bound(varNewValue) == 8) ) {
            var startHour =  ToInteger(SubStr(ToString(varNewValue), 0, 2));
            var startMin  =  ToInteger(SubStr(ToString(varNewValue), 2, 2));
            var endHour   =  ToInteger(SubStr(ToString(varNewValue), 4, 2));
            var endMin    =  ToInteger(SubStr(ToString(varNewValue), 6, 2));
            var newHour   = "";
            var newMin    = "";

            // 시, 분 차 구하기
            if(startHour >= endHour) {
                newHour = ToString(startHour - endHour);
            }
            else if(startHour < endHour) {
                newHour = ToString(endHour - startHour);
            }

            if(startMin >= endMin) {
                newMin = ToString(startMin - endMin);
            }
            else if(startMin < endMin) {
                newMin = ToString(endMin - startMin);
            }

            // 자릿수 재조정
            if(Bound(newHour) == 1) {
                newHour = "0" + newHour;
            }

            if(Bound(newMin) == 1) {
                newMin = "0" + newMin;
            }

            dsWkSche.SetColumn(nRow, "TIME", newHour+":"+newMin);
        }
        else {

            fnc_Message(SNIS_CAN_M001, "N");
        }

    }
}

function fcPrint(){
	if(cbWeekPart.Value == null){
		fnc_Message("주차가 전체 조회 일때는 인쇄할 수 없습니다.", "N", "");
		return;
	}

	var sServiceName  = "d06000004-service:searchPrint";
    var sInDataSet    = "";
    var sOutDataSet   = " dsWkSchePrint=dsWkSchePrint ";

    fcd_AddParam("rPerioNo", cbPerioNo.Value);
	fcd_AddParam("wKweekPart", cbWeekPart.Value); 

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);   
}

function fcPrintDetail()
{
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "d06000004.jrf";          //레포트 파일명
	var sDataSet  = " dsWkSchePrint=dsWkSchePrint dsCurEduStat=dsCurEduStat ";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "PerioNo=" + cbPerioNo.Value;
		sArg 	 += " Year="   + cbYearList.Value;
		sArg 	 +=	" Month="  + cbMonthList.Text;
	    sArg 	 += " Week="   + cbWeekPart.Text ;
		sArg     += " sdate="  + meStart.Value; 
		sArg 	 += " edate="  + meEnd.Value ;

	var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

function fcGetNum(a){
    var num = 0;
	for(var i = 0;i<dsListNum.RowCount();i++){
		if(a == dsListNum.GetColumn(i,"SEQ")){
		    num = dsListNum.GetColumn(i,"NUM");
		}
	}
	return num;
}

function fcCopyCheck(){

	var Cnt = dsWkSche.RowCount();

	if ( Cnt > 0 ){
		fnc_Message(SNIS_CAN_J006, "N", "");
		return false;
	}
	 return true;

}

function cbWeekPart_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if	(cbWeekPart.Value == null){
		grdWkSche.Editable = false;
	}
	else {
		grdWkSche.Editable = true;
	}
	fcSearch();
}


function btnMMSche_OnClick(obj)
{
	if( fnc_IsNull(cbMM.Value) ) {
        fnc_Message(SNIS_CAN_0008, "N", "월");
		return;
	} 
	if( fnc_IsNull(cbPerioNo.Value) ) {
        fnc_Message(SNIS_CAN_0008, "N", "기수");
		return; 
	} 

    var sServiceName = "d06000004-service:searchMMSche";
    var sInDataSet   = "";
    var sOutDataSet  = " dsMMSche=dsMMSche ";

	fcd_AddParam("rPerioNo",  cbPerioNo.Value);              
    fcd_AddParam("rMM",  cbMM.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
