/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'T'+sInpt+'FTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

	// 화면 초기화
	fcInit();

	//기수쿼리
	fcPerioNo();
	tabMain.TabIndex = 0;
	//검색조건 활성화 처리
	fcLoadEnable();	
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsBasItem|dsBodyInsp|dsStrtMesur|dsIdentInq|dsWrtn|dsHmntrInsp|dsInvw|dsEndRslt";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}


/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {

		if(tabMain.TabIndex == 0){
			dsBasItem.ClearData();
		}else if(tabMain.TabIndex == 1){
			dsBodyInsp.ClearData();	
		}else if(tabMain.TabIndex == 2){
			dsStrtMesur.ClearData();	
		}else if(tabMain.TabIndex == 3){
			dsIdentInq.ClearData();	
		}else if(tabMain.TabIndex == 4){
			dsWrtn.ClearData();	
		}else if(tabMain.TabIndex == 5){
			dsHmntrInsp.ClearData();	
		}else if(tabMain.TabIndex == 6){
			dsInvw.ClearData();	
		}else if(tabMain.TabIndex == 7){
			dsEndRslt.ClearData();
		}
    }    
}

