/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck() {

	// 시간배정표 과목 중복입력 체크
	if(tabMain.TabIndex == 0){
		if(fnc_IsColumnDuplicate(dsBasItem, "ITEM")) {
			fnc_Message(SNIS_CAN_0009, "N", "종목이");	
			return false;    
		}
	}

	return true;
}


function fcSave(obj) {

   if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

	var pk1		= "";
	var pk2 	= ""; 	//최종결과 pk
	var pk3 	= ""; 	//신원조회 pk
	var iScr	= 0; 	//점수
	var iChngScr= 0;	//환산점수

	var sRslt 	= "";	//신원조회 결과값
	var sItem	= "";	//선발기준종목
	var sItemRate = "";	//선발기준종목 배점비율

	if(tabMain.TabIndex == 0){	//선발기준종목		       
		sServiceName  = "d06000006-service:saveSelt";
		sInDataSet    = "dsBasItem=dsBasItem:u ";
		sOutDataSet   = "dsBasItem=dsBasItem ";
		for(var i=0; i<dsBasItem.rowcount; i++) {   
			dsBasItem.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
		}
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	}else if(tabMain.TabIndex == 1){ //신체검사	
		sServiceName  = "d06000006-service:saveSelt";
		sInDataSet    = "dsBodyInsp=dsBodyInsp:u ";
		sOutDataSet   = "dsBodyInsp=dsBodyInsp ";

		if(length(tabMain.tab2.CalSearchDt.Value) <= 0){	
			//날짜는 필수 저장
			fnc_Message(SNIS_CAN_A003, "N");
			return;
		}

		for(var i=0; i<dsBodyInsp.rowcount; i++) {   
			dsBodyInsp.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
			dsBodyInsp.SetColumn(i, "DT", Trim(tabMain.tab2.CalSearchDt.Value));			
		}
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	}else if(tabMain.TabIndex == 2){ //기초체력측정

		if(length(tabMain.tab3.CalSearchDt.Value) <= 0){	
			//날짜는 필수 저장
			fnc_Message(SNIS_CAN_A003, "N");
			return;
		}

		if(dsBasItem.rowcount > 0){ //선발기준종목 입력체크
			for(var i=0; i<dsBasItem.rowcount; i++) {  
				sItem = dsBasItem.GetColumn(i, "ITEM");
				if(sItem == "001"){
					sItemRate = dsBasItem.GetColumn(i, "ASSGN_SCR_RATE");
					break;
				}
			}

			if(Trim(sItemRate) != ""){ // 기초체력 배점비율 입력체크
				sServiceName  = "d06000006-service:saveSelt";
				sInDataSet    = "dsStrtMesur=dsStrtMesur:u ";
				sOutDataSet   = "dsStrtMesur=dsStrtMesur ";

				for(var i=0; i<dsStrtMesur.rowcount; i++) {   
					dsStrtMesur.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
					dsStrtMesur.SetColumn(i, "DT", Trim(tabMain.tab3.CalSearchDt.Value));		
					pk1 = dsStrtMesur.GetColumn(i, "RACER_PERIO_NO") + dsStrtMesur.GetColumn(i, "REPT_NO");

					iScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_01"));
					iScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_02"));
					iScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_03"));
					iScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_04"));
					iScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_05"));
					iScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_06"));
					iScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_07"));
					iScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_08"));
					//iScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_09"));
					//iScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_10"));
					//iScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_11"));
					iScr = iScr / 8;

					//신원조회 결과조회
					for(var k=0; k<dsIdentInq.rowcount; k++) {
						pk3 = dsIdentInq.GetColumn(k, "RACER_PERIO_NO") + dsIdentInq.GetColumn(k, "REPT_NO");				
						if(pk1 == pk3){
							sRslt = dsIdentInq.GetColumn(k, "RSLT"); 
						}
					}				

					//최종결과 셋팅
					for(var j=0; j<dsEndRslt.rowcount; j++) {
						pk2 = dsEndRslt.GetColumn(j, "RACER_PERIO_NO") + dsEndRslt.GetColumn(j, "REPT_NO");				
						if(pk1 == pk2){
							if(sRslt == "001"){ //합격
								dsEndRslt.SetColumn(j, "STRT_MESUR_SCR",iScr); 
								sItemRate = toNumber(sItemRate);
								iChngScr = iScr * sItemRate * 0.01;
								dsEndRslt.SetColumn(j, "STRT_MESUR_CHNG_SCR",iChngScr); 
							}			
						}
					}	
					iScr = 0;
					iChngScr = 0;
					sRslt = "";

				}				
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

				sServiceName  = "d06000006-service:saveSelt";
				sInDataSet    = "dsEndRslt=dsEndRslt:u ";
				sOutDataSet   = "dsEndRslt=dsEndRslt ";		
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
			}// 기초체력 배점비율 입력체크
		}//선발기준종목 입력체크

	}else if(tabMain.TabIndex == 3){ //신원조회

		if(length(tabMain.tab4.CalSearchDt.Value) <= 0){	
			//날짜는 필수 저장
			fnc_Message(SNIS_CAN_A003, "N");
			return;
		}

		for(var i=0; i<dsIdentInq.rowcount; i++) {   
			dsIdentInq.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
			dsIdentInq.SetColumn(i, "DT", Trim(tabMain.tab4.CalSearchDt.Value));	
			pk3 = dsIdentInq.GetColumn(i, "RACER_PERIO_NO") + dsIdentInq.GetColumn(i, "REPT_NO");
			sRslt = dsIdentInq.GetColumn(k, "RSLT");

			//최종결과 셋팅
			for(var j=0; j<dsEndRslt.rowcount; j++) {
				pk2 = dsEndRslt.GetColumn(j, "RACER_PERIO_NO") + dsEndRslt.GetColumn(j, "REPT_NO");
				if(pk1 == pk2){
					if(sRslt == "001"){ //합격
						dsEndRslt.SetColumn(j, "INQ_CD",sRslt); 
						dsEndRslt.GetColumn(j, "RACER_PERIO_NO",dsIdentInq.GetColumn(i, "RACER_PERIO_NO"));	
						dsEndRslt.GetColumn(j, "REPT_NO",dsIdentInq.GetColumn(i, "REPT_NO"));	
					}
				}
			}			

		}
		//신원조회 
		sServiceName  = "d06000006-service:saveSelt";
		sInDataSet    = "dsIdentInq=dsIdentInq:u ";
		sOutDataSet   = "dsIdentInq=dsIdentInq ";		
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");	

		//최종결과 셋팅	    
		sServiceName  = "d06000006-service:saveSelt";
		sInDataSet    = "dsEndRslt=dsEndRslt:u ";
		sOutDataSet   = "dsEndRslt=dsEndRslt ";			
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");				    
	}else if(tabMain.TabIndex == 4){ //필기

		if(length(tabMain.tab5.CalSearchDt.Value) <= 0){	
			//날짜는 필수 저장
			fnc_Message(SNIS_CAN_A003, "N");
			return;
		}

		if(dsBasItem.rowcount > 0){ //선발기준종목 입력체크
			for(var i=0; i<dsBasItem.rowcount; i++) {  
				sItem = dsBasItem.GetColumn(i, "ITEM");
				if(sItem == "002"){
					sItemRate = dsBasItem.GetColumn(i, "ASSGN_SCR_RATE");
					break;
				}
			}

			if(Trim(sItemRate) != ""){ // 기초체력 배점비율 입력체크	
				sServiceName  = "d06000006-service:saveSelt";
				sInDataSet    = "dsWrtn=dsWrtn:u ";
				sOutDataSet   = "dsWrtn=dsWrtn ";
				for(var i=0; i<dsWrtn.rowcount; i++) {   
					dsWrtn.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
					dsWrtn.SetColumn(i, "DT", Trim(tabMain.tab5.CalSearchDt.Value));
					pk1 = dsWrtn.GetColumn(i, "RACER_PERIO_NO") + dsWrtn.GetColumn(i, "REPT_NO");			
					iScr = toNumber(dsWrtn.GetColumn(i,"SCR"));

					//신원조회 결과조회
					for(var k=0; k<dsIdentInq.rowcount; k++) {
						pk3 = dsIdentInq.GetColumn(k, "RACER_PERIO_NO") + dsIdentInq.GetColumn(k, "REPT_NO");				
						if(pk1 == pk3){
							sRslt = dsIdentInq.GetColumn(k, "RSLT"); 
						}
					}	

					//최종결과 셋팅
					for(var j=0; j<dsEndRslt.rowcount; j++) {
						pk2 = dsEndRslt.GetColumn(j, "RACER_PERIO_NO") + dsEndRslt.GetColumn(j, "REPT_NO");
						if(pk1 == pk2){
							if(sRslt == "001"){ //합격
								dsEndRslt.SetColumn(j, "WRTN_SCR",iScr); 
								sItemRate = toNumber(sItemRate);
								iChngScr = iScr * sItemRate * 0.01;
								dsEndRslt.SetColumn(j, "WRTN_CHNG_SCR",iChngScr); 								
							}
						}
					}
					iScr = 0;
					iChngScr = 0;
					sRslt = "";
				}
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

				sServiceName  = "d06000006-service:saveSelt";
				sInDataSet    = "dsEndRslt=dsEndRslt:u ";
				sOutDataSet   = "dsEndRslt=dsEndRslt ";		
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
			}// 기초체력 배점비율 입력체크
		}//선발기준종목 입력체크				
	}else if(tabMain.TabIndex == 5){ //인성검사

		if(length(tabMain.tab6.CalSearchDt.Value) <= 0){	
			//날짜는 필수 저장
			fnc_Message(SNIS_CAN_A003, "N");
			return;
		}

		if(dsBasItem.rowcount > 0){ //선발기준종목 입력체크
			for(var i=0; i<dsBasItem.rowcount; i++) {  
				sItem = dsBasItem.GetColumn(i, "ITEM");
				if(sItem == "004"){
					sItemRate = dsBasItem.GetColumn(i, "ASSGN_SCR_RATE");
					break;
				}
			}

			if(Trim(sItemRate) != ""){ // 인성검사 배점비율 입력체크

				sServiceName  = "d06000006-service:saveSelt";
				sInDataSet    = "dsHmntrInsp=dsHmntrInsp:u ";
				sOutDataSet   = "dsHmntrInsp=dsHmntrInsp ";

				for(var i=0; i<dsHmntrInsp.rowcount; i++) {   
					dsHmntrInsp.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
					dsHmntrInsp.SetColumn(i, "DT", Trim(tabMain.tab6.CalSearchDt.Value));
					pk1 = dsHmntrInsp.GetColumn(i, "RACER_PERIO_NO") + dsHmntrInsp.GetColumn(i, "REPT_NO");
					iScr = toNumber(dsHmntrInsp.GetColumn(i,"SCR"));

					//신원조회 결과조회
					for(var k=0; k<dsIdentInq.rowcount; k++) {
						pk3 = dsIdentInq.GetColumn(k, "RACER_PERIO_NO") + dsIdentInq.GetColumn(k, "REPT_NO");				
						if(pk1 == pk3){
							sRslt = dsIdentInq.GetColumn(k, "RSLT"); 
						}
					}	

					//최종결과 셋팅			
					for(var j=0; j<dsEndRslt.rowcount; j++) {
						pk2 = dsEndRslt.GetColumn(j, "RACER_PERIO_NO") + dsEndRslt.GetColumn(j, "REPT_NO");
						if(pk1 == pk2){
							if(sRslt == "001"){ //합격				
								dsEndRslt.SetColumn(j, "HMNTR_INSP",iScr); 
								sItemRate = toNumber(sItemRate);
								iChngScr = iScr * sItemRate * 0.01;
								dsEndRslt.SetColumn(j, "HMNTR_INSP_CHNG_SCR",iChngScr); 
							}
						}
					}			
					iScr = 0;
					iChngScr = 0;
					sRslt = "";
				}
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

				sServiceName  = "d06000006-service:saveSelt";
				sInDataSet    = "dsEndRslt=dsEndRslt:u ";
				sOutDataSet   = "dsEndRslt=dsEndRslt ";		
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");	
			}
		}	
	}else if(tabMain.TabIndex == 6){ //면접

		if(length(tabMain.tab7.CalSearchDt.Value) <= 0){	
			//날짜는 필수 저장
			fnc_Message(SNIS_CAN_A003, "N");
			return;
		}

		if(dsBasItem.rowcount > 0){ //선발기준종목 입력체크
			for(var i=0; i<dsBasItem.rowcount; i++) {  
				sItem = dsBasItem.GetColumn(i, "ITEM");
				if(sItem == "003"){
					sItemRate = dsBasItem.GetColumn(i, "ASSGN_SCR_RATE");
					break;					
				}
			}

			if(Trim(sItemRate) != ""){ // 기초체력 배점비율 입력체크
				sServiceName  = "d06000006-service:saveSelt";
				sInDataSet    = "dsInvw=dsInvw:u ";
				sOutDataSet   = "dsInvw=dsInvw ";
				for(var i=0; i<dsInvw.rowcount; i++) {   
					dsInvw.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
					dsInvw.SetColumn(i, "DT", Trim(tabMain.tab7.CalSearchDt.Value));
					pk1 = dsInvw.GetColumn(i, "RACER_PERIO_NO") + dsInvw.GetColumn(i, "REPT_NO");
					iScr = toNumber(dsInvw.GetColumn(i,"SCR"));

					//신원조회 결과조회
					for(var k=0; k<dsIdentInq.rowcount; k++) {
						pk3 = dsIdentInq.GetColumn(k, "RACER_PERIO_NO") + dsIdentInq.GetColumn(k, "REPT_NO");				
						if(pk1 == pk3){
							sRslt = dsIdentInq.GetColumn(k, "RSLT"); 
						}
					}	

					//최종결과 셋팅			
					for(var j=0; j<dsEndRslt.rowcount; j++) {
						pk2 = dsEndRslt.GetColumn(j, "RACER_PERIO_NO") + dsEndRslt.GetColumn(j, "REPT_NO");
						if(pk1 == pk2){
							if(sRslt == "001"){ //합격
								dsEndRslt.SetColumn(j, "INVW_SCR",iScr); 
								sItemRate = toNumber(sItemRate);
								iChngScr = iScr * sItemRate * 0.01;
								dsEndRslt.SetColumn(j, "INVW_CHNG_SCR",iChngScr); 									
							}
						}
					}	
					iScr = 0;
					iChngScr = 0;
					sRslt = "";		
				}
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

				sServiceName  = "d06000006-service:saveSelt";
				sInDataSet    = "dsEndRslt=dsEndRslt:u ";
				sOutDataSet   = "dsEndRslt=dsEndRslt ";		
				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");	
			}// 기초체력 배점비율 입력체크
		}//선발기준종목 입력체크							
	}else if(tabMain.TabIndex == 7){ //최종결과
		sServiceName  = "d06000006-service:saveSelt";
		sInDataSet    = "dsEndRslt=dsEndRslt:u ";
		sOutDataSet   = "dsEndRslt=dsEndRslt ";
		for(var i=0; i<dsEndRslt.rowcount; i++) {   
			dsEndRslt.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
			//dsEndRslt.SetColumn(i, "DT", Trim(CalSearchDt.Value));
		}
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	}
}

