/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {


    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    var sDt = "";
	switch(sServiceName) {
		case "d06000006-service:searchBodyInsp" :
			//신체검사
		    for(var i=0; i<dsBodyInsp.rowcount; i++) {

		        if(fnc_IsNull( dsBodyInsp.GetColumn(i, "DT"))) {
		        } else {
					tabMain.tab2.CalSearchDt.Value = dsBodyInsp.GetColumn(i, "DT");
					break;
			    }		
			}

			break;
		case "d06000006-service:searchStrtMesur" :
			//기초체력측정
		    for(var i=0; i<dsStrtMesur.rowcount; i++) {

				if(fnc_IsNull( dsStrtMesur.GetColumn(i, "DT"))) {
		        } else {
					tabMain.tab3.CalSearchDt.Value = dsStrtMesur.GetColumn(i, "DT");
					break; 
			    }	 
			}

			break;
		case "d06000006-service:searchIdentInq" :
			//신원조회
		    for(var i=0; i<dsIdentInq.rowcount; i++) {

				if(fnc_IsNull( dsIdentInq.GetColumn(i, "DT"))) {
		        } else {
					tabMain.tab4.CalSearchDt.Value = dsIdentInq.GetColumn(i, "DT");
				    break; 
			    }	 
			}

			break;	
		case "d06000006-service:searchWrtn" :
			//필기
		    for(var i=0; i<dsWrtn.rowcount; i++) {

				if(fnc_IsNull( dsWrtn.GetColumn(i, "DT"))) {
		        } else {
					tabMain.tab5.CalSearchDt.Value = dsWrtn.GetColumn(i, "DT");
					break; 
			    }	 
			}

			break;		
		case "d06000006-service:searchHmntrInsp" :
			//인성검사
		    for(var i=0; i<dsHmntrInsp.rowcount; i++) {

				if(fnc_IsNull( dsHmntrInsp.GetColumn(i, "DT"))) {
		        } else {
					tabMain.tab6.CalSearchDt.Value = dsHmntrInsp.GetColumn(i, "DT");
					break; 
			    }	 
			}

			break;
		case "d06000006-service:searchInvw" :
			//면접
		    for(var i=0; i<dsInvw.rowcount; i++) {

				if(fnc_IsNull( dsInvw.GetColumn(i, "DT"))) {
		        } else {
					tabMain.tab7.CalSearchDt.Value = dsInvw.GetColumn(i, "DT");
					break; 
			    }	 
			}

			break;		
		case "d06000006-service:searchEndRslt" :
			//최종결과
			break;	 				

		case "d06000016-service:searchStrtMesurRound5_1" :
			break;

        case "d06000006-service:saveSelt" :
            fcSearch();
            break;

		default :
			break;  
	}      

	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

