//전년도 복사
function fcPerioNoCopy() {
    var dsPerioNoCount 	= dsPerioNo.GetRowCount();
    var iCbPerioNo 		= eval(Trim(cbPerioNo.Value));
    var iMinusPerioNo 	= iCbPerioNo-1;
    var selectPerioNo 	= ""; //선택기수 
    var finalPerioNo 	= ""; //최종기수

	var sServiceName 	= "";
	var sInDataSet		= "";
	var sOutDataSet		= "";

	//전기수 값 구하기
	for(var i=0; i<iCbPerioNo; i++) {
		selectPerioNo = fcPerioNoSelect(iMinusPerioNo);
		if(selectPerioNo > 0){
			finalPerioNo = selectPerioNo;
			break;
		}
		iMinusPerioNo--;  
	}

	if(finalPerioNo != ""){
		if(tabMain.TabIndex == 0){
			sServiceName = "d06000006-service:searchBasItem";
			sInDataSet   = "";
			sOutDataSet  = " dsBasItem=dsBasItem ";
			fcd_AddParam("racerPerioNo", finalPerioNo);
		}
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
		sCopy = "Y";
	}else{
		fnc_Message(SNIS_CAN_A011, "N");
	}
}

//마이너스 한 값이 현재 기수가 있는 체크하여 있으면 기수값 리턴
function fcPerioNoSelect(str){
    var dsPerioNoCount = dsPerioNo.GetRowCount();
    var retValue = 0;
	for(var i=0; i<dsPerioNoCount; i++) {   
		selectPerioNo = dsPerioNo.GetColumn(i,"RACER_PERIO_NO");
		if(str == selectPerioNo){
			retValue = selectPerioNo;
		}
	}
	return retValue;	
}

function dsStrtMesur_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	fcSetScr_dsStrtMesur(nRow, strColumnID, varNewValue);

}

function fcSetScr_dsStrtMesur(nRow, strColumnID, varNewValue) {
	var iRec = ToFloat(varNewValue);
	var iScr = 0;

	var sexGbn = dsStrtMesur.getColumn(nRow,"SEX_GBN");
	switch(strColumnID){
		case "REC_01"  : dsMesurBas.Filter("SEX_GBN='"+sexGbn+"' AND ITEM_CD ='001'"); break; //악력
		case "REC_02"  : dsMesurBas.Filter("SEX_GBN='"+sexGbn+"' AND ITEM_CD ='002'"); break; //윗몸일으키기
		case "REC_03"  : dsMesurBas.Filter("SEX_GBN='"+sexGbn+"' AND ITEM_CD ='003'"); break; //팔굽혀펴기
		case "REC_04"  : dsMesurBas.Filter("SEX_GBN='"+sexGbn+"' AND ITEM_CD ='004'"); break; //전신반응
		case "REC_05"  : dsMesurBas.Filter("SEX_GBN='"+sexGbn+"' AND ITEM_CD ='005'"); break; //앉아윗몸앞으로굽히기
		case "REC_06"  : dsMesurBas.Filter("SEX_GBN='"+sexGbn+"' AND ITEM_CD ='006'"); break; //제자리높이띄기
		case "REC_07"  : dsMesurBas.Filter("SEX_GBN='"+sexGbn+"' AND ITEM_CD ='007'"); break; //스텝테스트
		case "REC_08"  : dsMesurBas.Filter("SEX_GBN='"+sexGbn+"' AND ITEM_CD ='008'"); break; //근관절기능검사
		//case "REC_09"  : dsMesurBas.Filter("SEX_GBN='"+sexGbn+"' AND ITEM_CD ='009'"); break; //심폐지구력
		//case "REC_10"  : dsMesurBas.Filter("SEX_GBN='"+sexGbn+"' AND ITEM_CD ='010'"); break; //순발력
		//case "REC_11"  : dsMesurBas.Filter("SEX_GBN='"+sexGbn+"' AND ITEM_CD ='011'"); break; //운동부하검사
	    default : break;
	}

	for(i=0; i < dsMesurBas.RowCount();i++){
			var iStartRec = toNumber(dsMesurBas.GetColumn(i, "BAS_FROM_SCR")); 
			var iEndRec   = toNumber(dsMesurBas.GetColumn(i, "BAS_TO_SCR"));
			if( (iRec >= iStartRec)&&(iRec <=iEndRec)){
				 iScr = dsMesurBas.getColumn(i,"ASSIGN_SCR");
			 }
	}

	switch(strColumnID){
		case "REC_01"  : dsStrtMesur.SetColumn(nRow,"SCR_01",iScr);   break;
		case "REC_02"  : dsStrtMesur.SetColumn(nRow,"SCR_02",iScr);   break;
		case "REC_03"  : dsStrtMesur.SetColumn(nRow,"SCR_03",iScr);   break;
		case "REC_04"  : dsStrtMesur.SetColumn(nRow,"SCR_04",iScr);  	break;
		case "REC_05"  : dsStrtMesur.SetColumn(nRow,"SCR_05",iScr);  	break;
		case "REC_06"  : dsStrtMesur.SetColumn(nRow,"SCR_06",iScr);  	break;
		case "REC_07"  : dsStrtMesur.SetColumn(nRow,"SCR_07",iScr);  	break;
		case "REC_08"  : dsStrtMesur.SetColumn(nRow,"SCR_08",iScr);  	break;
		//case "REC_09"  : dsStrtMesur.SetColumn(nRow,"SCR_09",iScr);  	break;
		//case "REC_10"  : dsStrtMesur.SetColumn(nRow,"SCR_10",iScr);  	break;
		//case "REC_11"  : dsStrtMesur.SetColumn(nRow,"SCR_11",iScr);  	break;
		default : break;
	}
	//fcAvgScr(obj, nRow);// 평균,환산점수


	var scrCnt = 0;
	var totScr = 0;
	var avgScr = 0;


	if( Trim(strColumnID) == "SCR_01" || Trim(strColumnID) == "SCR_02" || 
		Trim(strColumnID) == "SCR_03" || Trim(strColumnID) == "SCR_04" || 
		Trim(strColumnID) == "SCR_05" || Trim(strColumnID) == "SCR_06" ||
		Trim(strColumnID) == "SCR_07" || Trim(strColumnID) == "SCR_08" 
		//Trim(strColumnID) == "SCR_09" || Trim(strColumnID) == "SCR_10" ||
		//Trim(strColumnID) == "SCR_11"
		){

		for(var i=0; i<dsStrtMesur.rowcount; i++) {  
			if(i == nRow){

				if(toNumber(dsStrtMesur.GetColumn(i,"SCR_01")) >= 0){
					scrCnt++;
					totScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_01"));
				}
				if(toNumber(dsStrtMesur.GetColumn(i,"SCR_02")) >= 0){
					scrCnt++;
					totScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_02"));
				}
				if(toNumber(dsStrtMesur.GetColumn(i,"SCR_03")) >= 0){
					scrCnt++;
					totScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_03"));
				}
				if(toNumber(dsStrtMesur.GetColumn(i,"SCR_04")) >= 0){
					scrCnt++;
					totScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_04"));
				}				
				if(toNumber(dsStrtMesur.GetColumn(i,"SCR_05")) >= 0){
					scrCnt++;
					totScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_05"));
				}

				if(toNumber(dsStrtMesur.GetColumn(i,"SCR_06")) >= 0){
					scrCnt++;
					totScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_06"));
				}

				if(toNumber(dsStrtMesur.GetColumn(i,"SCR_07")) >= 0){
					scrCnt++;
					totScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_07"));
				}

	            if(toNumber(dsStrtMesur.GetColumn(i,"SCR_08")) >= 0){
					scrCnt++;
					totScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_08"));
				}
				/*
	            if(toNumber(dsStrtMesur.GetColumn(i,"SCR_09")) > 0){
					scrCnt++;
					totScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_09"));
				}

	            if(toNumber(dsStrtMesur.GetColumn(i,"SCR_10")) > 0){
					scrCnt++;
					totScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_10"));
				}
				*/
				/*
	            if(toNumber(dsStrtMesur.GetColumn(i,"SCR_11")) > 0){
					scrCnt++;
					totScr += toNumber(dsStrtMesur.GetColumn(i,"SCR_11"));
				}
				*/
				avgScr = totScr / scrCnt;
				avgScr = ToFloat(avgScr);

				dsStrtMesur.SetColumn(i,"TOT_SCR",totScr); //총점 셋팅
				dsStrtMesur.SetColumn(i,"AVG_SCR",Round(avgScr,2)); //평균점수 셋팅
				break;
			}
		}
	}

}

//신체검사 엑셀등록
function cmdExcelUpload1_OnClick(obj)
{
	var strFileName;
	var nRow;
	var TR_AMT_IN;
	var TR_AMT_OUT;

	FileDialog.Type = "OPEN";
	FileDialog.Filter = "Excel(.xls)|*.xls|";

	if (!FileDialog.open()){
		return;
	}
	dsOutSheet.ClearData();
	dsBodyInsp.ClearData();
	strFileName = FileDialog.FilePath + "\\" + FileDialog.FileName;
	ext_ExcelImportByIndex(strFileName, 0, dsOutSheet.ID);
	nRow = dsOutSheet.RowCount();

	for (var i = 1; i < nRow; i++)
	{
		//dsStrtMesur.AddRow();
		//dsOutSheet.CopyToRow(i, dsStrtMesur, i - 1, "REPT_NO=Col02,NM_KOR=Col03,REC_1=Col04,SCR_1=Col05,REC_2=Col06,SCR_2=Col07,REC_3=Col08,SCR_3=Col09,REC_4=Col10,SCR_4=Col11,REC_5=Col12,SCR_5=Col13,REC_6=Col14,SCR_6=Col15,REC_7=Col16,SCR_7=Col17,REC_8=Col18,SCR_8=Col19,TOT_SCR=Col20,AVG_SCR=Col21,RANK=Col22");
		dsBodyInsp.AddRow();
		dsOutSheet.CopyToRow(i, dsBodyInsp, i - 1, "REPT_NO=Col02,NM_KOR=Col03,HGHT=Col04,WGHT=Col05,SIGHT_LEFT=Col06,SIGHT_RIGHT=Col07,BLOD_PRES=Col08,HEAR_LEFT=Col09,HEAR_RIGHT=Col10,COLOR_WEAK=Col11,ECG=Col12,CARDIAC_TEST=Col13,RSLT=Col14,IRREG_RSN=Col15");
	}

	for (var i = 0; i < dsBodyInsp.getRowCount(); i++)
	{
		if(dsBodyInsp.getColumn(i, "HEAR_LEFT") == "정상") {	//청력(좌)
			dsBodyInsp.setColumn(i, "HEAR_LEFT","001");
		} else {
			dsBodyInsp.setColumn(i, "HEAR_LEFT","002");		
		}

		if(dsBodyInsp.getColumn(i, "HEAR_RIGHT") == "정상") {	//청력(우)
			dsBodyInsp.setColumn(i, "HEAR_RIGHT","001");
		} else {
			dsBodyInsp.setColumn(i, "HEAR_RIGHT","002");	
		}

		if(dsBodyInsp.getColumn(i, "COLOR_WEAK") == "정상") {	//색신
			dsBodyInsp.setColumn(i, "COLOR_WEAK","001");
		} else {
			dsBodyInsp.setColumn(i, "COLOR_WEAK","002");		
		}

		if(dsBodyInsp.getColumn(i, "ECG") == "정상") {			//심전도
			dsBodyInsp.setColumn(i, "ECG","001");
		} else {
			dsBodyInsp.setColumn(i, "ECG","002");		
		}

		if(dsBodyInsp.getColumn(i, "CARDIAC_TEST") == "정상") {	//운동부하검사
			dsBodyInsp.setColumn(i, "CARDIAC_TEST","001");
		} else {
			dsBodyInsp.setColumn(i, "CARDIAC_TEST","002");		
		}

		if(dsBodyInsp.getColumn(i, "RSLT") == "정상") {			//결과
			dsBodyInsp.setColumn(i, "RSLT","001");
		} else {
			dsBodyInsp.setColumn(i, "RSLT","002");		
		}
	}
}

//기초체력측정 엑셀등록
function cmdExcelUpload2_OnClick(obj)
{
	var strFileName;
	var nRow;
	var TR_AMT_IN;
	var TR_AMT_OUT;

	FileDialog.Type = "OPEN";
	FileDialog.Filter = "Excel(.xls)|*.xls|";

	if (!FileDialog.open()){
		return;
	}
	dsOutSheet.ClearData();
	dsStrtMesur.ClearData();
	strFileName = FileDialog.FilePath + "\\" + FileDialog.FileName;
	ext_ExcelImportByIndex(strFileName, 0, dsOutSheet.ID);
	nRow = dsOutSheet.RowCount();

	for (var i = 1; i < nRow; i++)
	{
		dsStrtMesur.AddRow();
		//dsOutSheet.CopyToRow(i, dsStrtMesur, i - 1, "REPT_NO=Col02,NM_KOR=Col03,REC_1=Col04,SCR_1=Col05,REC_2=Col06,SCR_2=Col07,REC_3=Col08,SCR_3=Col09,REC_4=Col10,SCR_4=Col11,REC_5=Col12,SCR_5=Col13,REC_6=Col14,SCR_6=Col15,REC_7=Col16,SCR_7=Col17,REC_8=Col18,SCR_8=Col19,TOT_SCR=Col20,AVG_SCR=Col21,RANK=Col22");
		dsOutSheet.CopyToRow(i, dsStrtMesur, i - 1, "REPT_NO=Col02,NM_KOR=Col03,REC_01=Col04,REC_02=Col05,REC_03=Col06,REC_04=Col07,REC_05=Col08,REC_06=Col09,REC_07=Col10,REC_08=Col11,REC_11=Col12");

		//신원조회 DATASET에서 성별데이터를 가져온다
		dsStrtMesur.setColumn(i-1,"SEX_GBN",dsIdentInq.GetColumn(dsIdentInq.FindRow("REPT_NO",dsStrtMesur.getColumn(i-1,"REPT_NO")),"SEX_GBN"));
	}

	//총점, 평균, 순위 결정
	for (var i = 0; i < dsStrtMesur.getRowCount(); i++)
	{
		fcSetScr_dsStrtMesur(i, "REC_01", dsStrtMesur.getColumn(i,"REC_01"));
		fcSetScr_dsStrtMesur(i, "REC_02", dsStrtMesur.getColumn(i,"REC_02"));
		fcSetScr_dsStrtMesur(i, "REC_03", dsStrtMesur.getColumn(i,"REC_03"));
		fcSetScr_dsStrtMesur(i, "REC_04", dsStrtMesur.getColumn(i,"REC_04"));
		fcSetScr_dsStrtMesur(i, "REC_05", dsStrtMesur.getColumn(i,"REC_05"));
		fcSetScr_dsStrtMesur(i, "REC_06", dsStrtMesur.getColumn(i,"REC_06"));
		fcSetScr_dsStrtMesur(i, "REC_07", dsStrtMesur.getColumn(i,"REC_07"));
		fcSetScr_dsStrtMesur(i, "REC_08", dsStrtMesur.getColumn(i,"REC_08"));
		//fcSetScr_dsStrtMesur(i, "REC_09", dsStrtMesur.getColumn(i,"REC_09"));
		//fcSetScr_dsStrtMesur(i, "REC_10", dsStrtMesur.getColumn(i,"REC_10"));
		fcSetScr_dsStrtMesur(i, "REC_11", dsStrtMesur.getColumn(i,"REC_11"));
	}
}

function cmdSampleUpload_OnClick(obj)
{

	switch(obj.ID){
		case "btnFileDownload1"  : //신체검사
			ExecBrowser(GBL_FILE_UPDNLOAD_URL + "/sampleBodyInsp.xls"); 
			break;
		case "btnFileDownload2"  : //기초체력측정
			//ExecBrowser(GBL_FILE_UPDNLOAD_URL + "/sampleStrtMesur_rept.xls"); 
			ExecBrowser(GBL_FILE_UPDNLOAD_URL + "/sampleStrtMesur.xls"); 
			break;
		default : break;
	}

}
