/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'T'+sInpt+'FTTT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	// 화면 초기화
	fcInit();

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);
	tabMain.TabIndex = 0;
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

function fcFormUnloadProcess(obj) {

	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset 	 = "dsCandIdentList|dsCandIdent|dsFmly|dsSchlCarr|dsJobCarr|dsCarr|dsRacerReln";
		sDataset	+= "|dsInvwCntnt|dsPrizPenalScr|dsPriz|dsTempAbs|dsLicList|dsUploadFileList";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;

} 
/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}
/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Clear 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

  if(sInpt == "F"){
		btnCopy.Visible 	  = false;
  }

  if (obj.id == "btnCancel" || obj.id == "btnSearch") {
		cbRacerPerioNo.Index = -1;
		dsCandIdentList.ClearData();
        dsCandIdent.ClearData();

        dsFmly.ClearData();
        dsSchlCarr.ClearData();
        dsJobCarr.ClearData();
		dsCarr.ClearData();
		dsRacerReln.ClearData();
		dsInvwCntnt.ClearData();
		dsPrizPenalScr.ClearData();
		dsPriz.ClearData();
		dsTempAbs.ClearData();
        dsLicList.ClearData();
        dsUploadFileList.ClearData();
        //dsCandIdent.AddRow();
    }
}


//조회버튼 클릭 시
function fcSearch(obj) {

    if(fnc_IsNull(cbRacerPerioNo.Value)){
		fnc_Message(SNIS_CAN_0008, "N","기수");
		return;
	}

    var sServiceName = "d06000007-service:searchCandIdentList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsCandIdentList=dsCandIdentList";

    // 기수, 성명으로 후보생 신상 테이블을 조회하여 그리드에 출력한다.	
	fcd_AddParam("racerPerioNo", Trim(cbRacerPerioNo.Value));
	fcd_AddParam("nmKor", Trim(edNm.text));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false, "S");

	imgPhoto.ImageID = "";
}
/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {
	//후보생 정보.
	if(dsCandIdent.GetUpdate()){
		if(ToUpper(dsCandIdent.GetRowType(0)) == "INSERT"){
			dsCandIdent.SetColumn(0,"RACER_PERIO_NO",cbRacerPerioNo.Value);
		}

		if(fnc_IsNull(dsCandIdent.GetColumn(0,"RACER_PERIO_NO"))) {
			fnc_Message(SNIS_CAN_0008, "N", "기수");
			return false;
		}  

		if(fnc_IsNull(dsCandIdent.GetColumn(0,"NM_KOR"))) {
			fnc_Message(SNIS_COM_1034, "N", "성명(한글)");
			return false;
		}

		if(fnc_IsNull(dsCandIdent.GetColumn(0,"BIRTH"))) {
			fnc_Message(SNIS_COM_1034, "N", "생년월일");
			return false;
		}

		if(fnc_IsNull(dsCandIdent.GetColumn(0,"SEX"))) {
			fnc_Message(SNIS_COM_1034, "N", "성별");
			return false;
		}

		if(fnc_IsNull(dsCandIdent.GetColumn(0,"PHONE1"))){
			fnc_Message(SNIS_COM_1034, "N", "전화번호");
			return;
		}

		if(fnc_IsNull(dsCandIdent.GetColumn(0,"PHONE2"))){
			fnc_Message(SNIS_COM_1034, "N", "전화번호");
			return;
		}
		if(fnc_IsNull(dsCandIdent.GetColumn(0,"PHONE3"))){
			fnc_Message(SNIS_COM_1034, "N", "전화번호");
			return;
		} 
		if(fnc_IsNull(dsCandIdent.GetColumn(0,"CELLPHONE1"))){
			fnc_Message(SNIS_COM_1034, "N", "휴대폰번호");
			return;
		}

		if(fnc_IsNull(dsCandIdent.GetColumn(0,"CELLPHONE2"))){
			fnc_Message(SNIS_COM_1034, "N", "휴대폰번호");
			return;
		}
		if(fnc_IsNull(dsCandIdent.GetColumn(0,"CELLPHONE3"))){
			fnc_Message(SNIS_COM_1034, "N", "휴대폰번호");
			return;
		} 

		if(fnc_IsNull(dsCandIdent.GetColumn(0,"TH_STAT"))){
			fnc_Message(SNIS_CAN_0008,"N","이수");
			return false;
		}
		if(fnc_IsNull(dsCandIdent.GetColumn(0,"ADDR1"))){
			fnc_Message(SNIS_CAN_0008,"N","주소");
			return false;
		}
		if(fnc_IsNull(dsCandIdent.GetColumn(0,"ADDR2"))){
			fnc_Message(SNIS_CAN_0008,"N","상세주소");
			return false;
		}
	}
	//가족사항
	if(!checkFmly(dsFmly)){
		return false;
	}
	//학력사항
	if(!checkSchlCarr(dsSchlCarr)){
		return false;
	}
	//경력사항(직업)
	if(!checkJobCarr(dsJobCarr)){
		return false;
	} 
	//경력사항(대회)
	if(!checkCarr(dsCarr)){
		return false;
	}
	//친분관계
	if(!checkReln(dsRacerReln)){
		return false;
	}
	//면담사항
	if(!checkInvwCntnt(dsInvwCntnt)){
		return false;
	}
	//상벌점현황
	if(!checkPrizPenalScr(dsPrizPenalScr)){
		return false;
	}
	//포상현황
	if(!checkPriz(dsPriz)){
		return false;
	}
	//휴복학현황
	if(!checkTempAbs(dsTempAbs)){
		return false;
	}
	//자격면허
	if(!checkLicList(dsLicList)){
		return false;
	}
	//파일첨부
	if(!checkUploadFileList(dsUploadFileList)){
		return false;
	}
	return true;
}

//가족사항
function checkFmly(obj){
	var nFlag = true;
	var tabTitle = "가족사항";
	if(obj.GetUpdate()){
		for(var i=0;i<obj.GetRowCount();i++){
			if(fnc_IsNull(obj.GetColumn(i,"FMLY_RELN"))){
				fnc_Message("["+tabTitle+"]탭의 "+SNIS_CAN_0008,"N","관계");
				nFlag = false;
				return;
			}

			if(fnc_IsNull(obj.GetColumn(i,"NM_KOR"))){
				fnc_Message("["+tabTitle+"]탭의 "+SNIS_COM_1034,"N","성명");
				nFlag = false;
				return;
			}

		}
	}
	return nFlag;
}

