// 저장 공통
function fcSave(obj) {
	//변경된 자료가 있는지 체크
		if ( !fnc_DatasetChangeCheck(dsCandIdent) 
		  && !fnc_DatasetChangeCheck(dsFmly) 
		  && !fnc_DatasetChangeCheck(dsSchlCarr) 
		  && !fnc_DatasetChangeCheck(dsJobCarr) 
		  && !fnc_DatasetChangeCheck(dsCarr) 
		  && !fnc_DatasetChangeCheck(dsRacerReln) 
		  && !fnc_DatasetChangeCheck(dsInvwCntnt) 
		  && !fnc_DatasetChangeCheck(dsPrizPenalScr) 
		  && !fnc_DatasetChangeCheck(dsPriz) 
		  && !fnc_DatasetChangeCheck(dsTempAbs) 
		  && !fnc_DatasetChangeCheck(dsLicList) 
		  && !fnc_DatasetChangeCheck(dsUploadFileList)) {
			//저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");
			return false;
		}
		if(!fcSaveCheck()){
			return;
		}

		if(ToUpper(dsCandIdent.GetRowType(0)) == "INSERT"){//insert일 경우에만 키를 구한다.
			var sServiceName = "";
			var sInDataSet   = "";
			var sOutDataSet  = "";  

			sServiceName	 = "d06000007-service:keyCandIdent";
			sInDataSet		 = "";
			sOutDataSet		 = "dsCandSeq=dsCandSeq";

			fcd_AddParam("racerPerioNo", Trim(cbRacerPerioNo.Value));
			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
		}
		else{
			fcSaveProc();
		}
}

function fcSaveProc(nCandNo) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	if(Length(nCandNo)>0){//키가 넘어 왔을 경우에만 키를 셋팅. 아닐 경우는 그냥 처리.
		dsCandIdent.SetColumn(0,"CAND_NO",nCandNo);
	}

	sServiceName     = "d06000007-service:saveCandIdent";
	sInDataSet       = "dsCandIdent=dsCandIdent:a";
	sInDataSet      += " dsFmly=dsFmly:u";						//가족
	sInDataSet		+= " dsSchlCarr=dsSchlCarr:u";				//학력
	sInDataSet		+= " dsJobCarr=dsJobCarr:u";				//경력(직업)
	sInDataSet		+= " dsCarr=dsCarr:u";						//경력(대회)
	sInDataSet		+= " dsRacerReln=dsRacerReln:u";			//친분
	sInDataSet		+= " dsInvwCntnt=dsInvwCntnt:u";			//면담
	sInDataSet		+= " dsPrizPenalScr=dsPrizPenalScr:u";		//상벌점
	sInDataSet		+= " dsPriz=dsPriz:u";						//포상
	sInDataSet		+= " dsTempAbs=dsTempAbs:u";				//휴복학퇴소
	sInDataSet		+= " dsLicList=dsLicList:u";				//자격면허
	sInDataSet		+= " dsUploadFileList=dsUploadFileList:u";	//파일첨부

	sOutDataSet      = "";

	if(dsUploadFile.GetRowCount() >0 || dsUploadFileList.GetRowCount() > 0){
		saveFile();//파일저장

		if(dsUploadFile.GetRowCount()>0){
			dsCandIdent.SetColumn(0,"PTO_FILE_REAL_NM",dsUploadFile.GetColumn(0,"FILE_URL"));
		}

	}

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false, "U");
}

function saveFile(obj){
	if( dsUploadFile.GetRowCount() > 0 ) {

	     if(dsUploadFile.getColumn(0, "CHK") == "1" ) {
			var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
										, dsUploadFile.GetColumn(0, "FILE_URL" )
										, dsUploadFile.GetColumn(0, "FILE_NAME" )
										, "RACER");

			var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(0, "FILE_URL" ));

			//if ( rtn_arr[0] != "SUCC" ) {
			//	alert("파일 업로드 에러발생(사진)");
			//	return false;
			//}
			dsUploadFile.SetColumn(0, "FILE_URL"    , rtn_arr[1] );//실제 파일 경로.
			dsUploadFile.SetColumn(0, "FILE_SIZE"   , fileSize );
		} 	// end if
	} 		// end for  
	if( dsUploadFileList.GetRowCount() > 0 ) {

		for(var i=0 ; i < dsUploadFileList.GetRowCount() ; i++){
			if(dsUploadFileList.getColumn(i, "CHK") == "1" ) {
			var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
											, dsUploadFileList.GetColumn(i, "FILE_URL" )
											, dsUploadFileList.GetColumn(i, "FILE_NAME" )
											, "RACER");

				var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFileList.GetColumn(i, "FILE_URL" ));
				//if ( rtn_arr[0] != "SUCC" ) {
				//	alert("첨부파일 업로드 에러발생(첨부파일)");
				//	return false;
				//}

				dsUploadFileList.SetColumn(i, "FILE_URL"    , rtn_arr[1] );//실제 파일 경로.
				dsUploadFileList.SetColumn(i, "FILE_SIZE"   , fileSize );
			}  // end if
		}      // end for
	} 		  

}

