/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    switch(sServiceName){  
		case "common-service:searchCode" :
			cbRacerPerioNo.Index = 0;
			fcSearch(this);
			break;

        case "d06000007-service:searchCandIdentList" :
			if(dsCandIdentList.RowCount() > 0) {
				dsCandIdentList_OnRowPosChanged("","",0);				
			}
			break;

		case "d06000007-service:searchCandIdent":
			//이미지 set
			var img = split(dsCandIdent.GetColumn(0,"PTO_FILE_REAL_NM"),"/");
			imgPhoto.ImageID = GBL_IMAGELINK_URL+img[Length(img)-1];
			break;

		case "d06000007-service:keyCandIdent":
			fcSaveProc(dsCandSeq.GetColumn(0,"SEQ"));
			break;

        case "d06000007-service:saveCandIdent"  :  //후보생저장후
			dsUploadFile.ClearData();
			fcSearch();
			break;

		case "common-service:searchCode" :
			cbRacerPerioNo.Index = 0;
			break;

		case "d06000007-service:deleteCandIdent" :
			dsCandIdent.ClearData();
			dsFmly.ClearData();
			dsSchlCarr.ClearData();
			dsJobCarr.ClearData();
			dsCarr.ClearData();
			dsRacerReln.ClearData();
			dsInvwCntnt.ClearData();
			dsPrizPenalScr.ClearData();
			dsPriz.ClearData();
			dsTempAbs.ClearData();
			dsLicList.ClearData();
			dsUploadFileList.ClearData();

			imgPhoto.ImageID = "";

			fcSearch();
			break;

		case "d06000007-service:copyCandIdent" :
			fcSearch();
			break;
    }

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	switch(obj.GetForm().id) {
		case "divBtnRow":
			dsCandIdent.ClearData();
			dsCandIdent.AddRow();

			dsFmly.ClearData();
			dsSchlCarr.ClearData();
			dsJobCarr.ClearData();
			dsCarr.ClearData();
			dsRacerReln.ClearData();
			dsInvwCntnt.ClearData();
			dsPrizPenalScr.ClearData();
			dsPriz.ClearData();
			dsTempAbs.ClearData();
			dsLicList.ClearData();
			dsUploadFileList.ClearData();

			imgPhoto.ImageID = "";
			edNmKor.SetFocus();
			break;

		case "divBtnFam"://가족사항
			if(fcCheckHead()){
				dsFmly.AddRow();
			}
			break;

	    case "divBtnSchCarr"://학력사항
			if(fcCheckHead()){
				dsSchlCarr.AddRow();
			}
			break;
		 case "divBtnJobCarr"://경력
			if(fcCheckHead()){
				dsJobCarr.AddRow();
			}
			break; 
	    case "divBtnCarr"://경력(대회)
			if(fcCheckHead()){
				dsCarr.AddRow();
			}
			break;

	    case "divBtnReln": // 친분관계
			if(fcCheckHead()){
				dsRacerReln.AddRow();
			}
			break;

	    case "divBtndsInvw"://면담
			if(fcCheckHead()){
				dsInvwCntnt.AddRow();
			}
			break;

	    case "divBtnRwnpt"://상벌점
			if(fcCheckHead()){
				dsPrizPenalScr.AddRow();
				dsPrizPenalScr.SetColumn(nRow,"RWNPT_GBN","001");
			}
			break;

	    case "divBtnPrz"://포상
			if(fcCheckHead()){
				dsPriz.AddRow();
			}
			break;

	    case "divBtnTempAbs"://휴복학
			if(fcCheckHead()){
				dsTempAbs.AddRow();
			}
			break;
		case "divBtnLic":   //자격면허
			if(fcCheckHead()){
				var nRow = dsLicList.AddRow();
			}
			break;
		case "divBtnFile":   //파일첨부
			if(fcCheckHead()){
				fcFileSelect(obj);
			}
			break;	 
		}
	}
}

function fcCheckHead(){
	if(dsCandIdent.GetRowCount()==0){
		fnc_Message("후보생정보가 선택되어 있을경우에만 사용이 가능합니다.","N");
		return false;
	}
	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	if(dsCandIdent.GetRowCount()>0){//하단 리스트에서 클릭했을 경우에만 처리
		switch(obj.GetForm().id) {
		case "divBtnRow":
			if(dsCandIdent.GetRowCount() == 0){
				fnc_Message(SNIS_COM_1003,"N");
			}
			else if(confirm("선택된 정보를 삭제하시겠습니까?")){
				dsCandIdent.SetColumn(0,"DEL_YN","Y");

				sServiceName     = "d06000007-service:deleteCandIdent";
				sInDataSet       = "dsCandIdent=dsCandIdent:a";
				sOutDataSet      = "";

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
			}

			break;
		case "divBtnFam"://가족사항
			fnc_DeleteCheck(dsFmly, "CHK", "1");
			break;
		case "divBtnSchCarr"://학력사항
			fnc_DeleteCheck(dsSchlCarr,"CHK","1");
			break;
		case "divBtnJobCarr"://경력
			fnc_DeleteCheck(dsJobCarr,"CHK","1");
			break;
		case "divBtnCarr"://경력(대회)
			fnc_DeleteCheck(dsCarr,"CHK","1");
			break;
		case "divBtnReln": // 친분관계
			fnc_DeleteCheck(dsRacerReln,"CHK","1");
			break;
		case "divBtndsInvw"://면담
			fnc_DeleteCheck(dsInvwCntnt,"CHK","1");
			break;
		case "divBtnRwnpt"://상벌점
			fnc_DeleteCheck(dsPrizPenalScr,"CHK","1");
			break;
		case "divBtnPrz":	//포상
			fnc_DeleteCheck(dsPriz,"CHK","1");
			break;
		case "divBtnTempAbs"://휴복학
			fnc_DeleteCheck(dsTempAbs,"CHK","1");
			break;
		case "divBtnLic":    //자격면허
			fnc_DeleteCheck(dsLicList,"CHK","1");
			break;
		case "divBtnFile":   //첨부파일
			fnc_DeleteCheck(dsUploadFileList,"CHK","1");
			break;

		}
	}
	return;
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRowSub" ) {
         //var sArg = "";

         if(tabMain.TabIndex == 0){
		   if ( !fnc_DeleteCheck(dsFmly, "CHK", "1") ) 			return;
		 } // tab0가족사항)

		 if(tabMain.TabIndex == 1){
		   if ( !fnc_DeleteCheck(dsSchlCarr, "CHK", "1") ) 		return;
		 } // tab1(학력사항)
		 if(tabMain.TabIndex == 2){
		   if ( !fnc_DeleteCheck(dsJobCarr, "CHK", "1") ) 		return;
		 } // tab2(경력사항)

		 if(tabMain.TabIndex == 3){
		   if ( !fnc_DeleteCheck(dsCarr, "CHK", "1") ) 			return;
		 } // tab3(경력사항(대회))

		 if(tabMain.TabIndex == 4){
		   if ( !fnc_DeleteCheck(dsRacerReln, "CHK", "1") ) 	return;
		 } // tab4경륜선수와 관계)

		 if(tabMain.TabIndex == 5){
		   if ( !fnc_DeleteCheck(dsInvwCntnt, "CHK", "1") ) 	return;
		 } // tab5(면담사항)

		 if(tabMain.TabIndex == 6){
		   if ( !fnc_DeleteCheck(dsPrizPenalScr, "CHK", "1") ) 	return;
		 } // tab6상벌사항)

		 if(tabMain.TabIndex == 7){
		   if ( !fnc_DeleteCheck(dsPriz, "CHK", "1") )		   return;
		 } // tab7포상사항)

		 if(tabMain.TabIndex == 8){
		   if ( !fnc_DeleteCheck(dsTempAbs, "CHK", "1") ) 		return;
		 } // tab8휴/복학사항)
		 if(tabMain.TabIndex == 9){
		   if ( !fnc_DeleteCheck(dsLicList, "CHK", "1") ) 		return;
		 } // tab9자격면허
		 if(tabMain.TabIndex == 10){
		   if ( !fnc_DeleteCheck(dsUploadFileList, "CHK", "1") ) return;
		 } // tab10파일첨부

	} // if end	
}


//파일첨부
function fcFileSelect(obj){
	var sResponse = fcd_GetSelectFile();

	if(Length(sResponse) > 0) {

		if(obj.id == "btPto"){

		    	dsUploadFile.ClearData();
				var nRow = dsUploadFile.AddRow();

				dsUploadFile.SetColumn(nRow,"CHK","1");
				dsUploadFile.SetColumn(nRow,"FILE_URL",sResponse[0] + "\\" + sResponse[1]);
				dsUploadFile.SetColumn(nRow,"FILE_NAME",sResponse[1]);

				dsCandIdent.SetColumn(0,"PTO_FILE_VIEW_NM",sResponse[1]);				
				imgPhoto.ImageID = dsUploadFile.GetColumn(nRow,"FILE_URL"); 

		} else {
				var nRow = dsUploadFileList.AddRow();
				dsUploadFileList.SetColumn(nRow,"CHK","1");
				dsUploadFileList.SetColumn(nRow,"FILE_URL"	,sResponse[0] + "\\" + sResponse[1]);
				dsUploadFileList.SetColumn(nRow,"FILE_NAME"	,sResponse[1]);
		} 
	}
}

//상벌점 데이터셋 변경시 상점 벌점 계산을 위한 함수.
function dsPrizPenalScr_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex){
	if(strColumnID == "DUM_CD"){
		//보이기용 컬럼 변경시 실제컬럼을 변경처리.
		obj.SetColumn(nRow,"RWNPT_DETL_ITEM_CD",substr(varNewValue,3,3));
	}

	if(strColumnID == "RWNPT_DETL_ITEM_CD" || strColumnID == "RWNPT_GBN"){
		if(fnc_IsNull(obj.GetColumn(nRow,"RWNPT_DETL_ITEM_CD"))){
			return;
		}
		var sReturn = Array(2);
		var nFindRow = dsPenalCd.FindRow("PRIZ_PENAL_CD",obj.GetColumn(nRow,"DUM_CD"));

		sReturn[0] = decode(dsPenalCd.GetColumn(nFindRow,"ADD_SCR"),null,0,"",0,dsPenalCd.GetColumn(nFindRow,"ADD_SCR"));
		sReturn[1] = decode(dsPenalCd.GetColumn(nFindRow,"PENAL_SCR"),null,0,"",0,dsPenalCd.GetColumn(nFindRow,"PENAL_SCR"));

		//상점일경우와 벌점인경우 점수셋팅
		if(dsPrizPenalScr.GetColumn(nRow,"RWNPT_GBN")=="001"){	//상이면
			obj.SetColumn(nRow,"PRZ_SCR",sReturn[0]);
			obj.SetColumn(nRow,"PENAL_SCR","0");
		}
		else{//벌이면 
			obj.SetColumn(nRow,"PRZ_SCR","0");
			obj.SetColumn(nRow,"PENAL_SCR",sReturn[1]);
		}
	}
}

