// 후보생리스트 DataSet Row 변경 이벤트
function dsCandIdentList_OnRowPosChanged(obj,nOldRow,nRow)
{
    var sRacerPerioNo   =   dsCandIdentList.GetColumn(nRow,"RACER_PERIO_NO");
    var sCandNo         =   dsCandIdentList.getColumn(nRow,"CAND_NO");
    var sServiceName    = "d06000007-service:searchCandIdent";
    var sInDataSet      = "";
    var sOutDataSet     =  "dsCandIdent=dsCandIdent";
		sOutDataSet		+= " dsFmly=dsFmly";					//가족
		sOutDataSet		+= " dsSchlCarr=dsSchlCarr";			//학력
		sOutDataSet		+= " dsJobCarr=dsJobCarr";				//경력(직업)
		sOutDataSet		+= " dsCarr=dsCarr";					//경력(대회ㅒ
		sOutDataSet		+= " dsRacerReln=dsRacerReln";			//친분
		sOutDataSet		+= " dsInvwCntnt=dsInvwCntnt";			//면담
		sOutDataSet		+= " dsPrizPenalScr=dsPrizPenalScr";	//상벌점
		sOutDataSet		+= " dsPriz=dsPriz";					//포상
		sOutDataSet		+= " dsTempAbs=dsTempAbs";				//휴복학퇴소
        sOutDataSet		+= " dsLicList=dsLicList";				//자격면허
        sOutDataSet		+= " dsUploadFileList=dsUploadFileList";//파일첨부

    if((fnc_IsNull(sRacerPerioNo) || fnc_IsNull(sCandNo)) && cbRacerPerioNo.Index != -1){    
		fnc_Message("후보생리스트를 찾을 수 없습니다. 확인하시고 처리하시기 바랍니다.","N");

		dsCandIdent.ClearData();
		dsFmly.ClearData();
		dsSchlCarr.ClearData();
		dsJobCarr.ClearData();
		dsCarr.ClearData();
		dsRacerReln.ClearData();
		dsInvwCntnt.ClearData();
		dsPrizPenalScr.ClearData();
		dsPriz.ClearData();
		dsTempAbs.ClearData();
		dsLicList.ClearData();
		dsUploadFileList.ClearData();

		return;
	}

    fcd_AddParam("racerPerioNo", sRacerPerioNo);  //매개변수 : 기수
    fcd_AddParam("candNo"	   , sCandNo);        //매개변수 : 후보생번호

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false, "");
}
