/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크

	if ( !fnc_DatasetChangeCheck(dsClsOrgan) ) 
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return;
	}

	// 필수항목체크
	oDataSetClsOrgan 	= dsClsOrgan;

	sMandatoryColID   	= null;
	sMandatoryColName 	= null;
	oGrid             	= null;

	sMandatoryColID   	= "CAND_NO|NM_KOR";
	sMandatoryColName 	= "성명|성명";
	oGrid             	= grdClsOrgan;
	if ( fnc_GetMandatoryViolateRow(oDataSetDetlEduAssign, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}


