    sServiceName  = "d06000012-service:saveClsOrgan";

    sInDataSet    = "dsClsOrgan=dsClsOrgan:u ";
    sOutDataSet   = "dsClsOrgan=dsClsOrgan ";  

	for(var i=0; i<dsClsOrgan.rowcount; i++) {   
	//	dsClsOrgan.SetColumn(i, "CLS", Trim(cbInputCls.Value));	
