/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	switch(sServiceName) {


        case "d06000012-service:searchGudLectr1" :
 			dsGudLectr.InsertRow(0);
			dsGudLectr.SetColumn(0, "LECTR_NM", "선택");
			dsGudLectr.SetColumn(0, "GUD_LECTR_ID", "-1"); 
            break;            
		default :
			break;
	}	

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}


function dsCandNo_OnLoadComleted(obj,nErrorCode,strErrorMsg,nReason)
{
	dsCandNo.InsertRow(0);
	dsCandNo.SetColumn(0, "NM_KOR", "전체");	
}

function dsClsOrgan_OnLoadComleted(obj,nErrorCode,strErrorMsg,nReason)
{
	var sCls 			= "";
	var sQueryLectrId 	= "";
	var sQueryCls		= "";

	if(length(cbSearchCls.value) > 0){
		sCls = cbSearchCls.value;
	}

	if ( ToNumber(dsClsOrgan.GetRowCount()) == 0 ) return;

	for(var i=0; i<dsClsOrgan.rowcount; i++) {    
		sQueryLectrId= dsClsOrgan.GetColumn(i,"GUD_LECTR_ID");
		sQueryCls	 = dsClsOrgan.GetColumn(i,"CLS");
		break;
	}	

	cbGudLectr.value = sQueryLectrId;
	cbInputCls.value = sQueryCls;

}

//후보생 반편성 추가
function btAdd_OnClick(obj)
{
	var nRecordCount = dsCandIdent.RowCount();
	var nAddRow;

	//선택한 후보생 추가
	for (var i = 0; i < nRecordCount; i++)
	{
		if (ToString(dsCandIdent.GetColumn(i, "CHK")) == "1")
		{
			nAddRow = dsClsOrgan.AddRow();
			dsClsOrgan.SetColumn(nAddRow, "RACER_PERIO_NO", dsCandIdent.GetColumn(i, "RACER_PERIO_NO"));		
			dsClsOrgan.SetColumn(nAddRow, "NM_KOR", dsCandIdent.GetColumn(i, "NM_KOR"));
			dsClsOrgan.SetColumn(nAddRow, "CAND_NO", dsCandIdent.GetColumn(i, "CAND_NO"));
		}
	}

	//추가한 후보생 그리드에서 삭제 
	for (var i = nRecordCount; i >= 0; i--)
	{
		if (ToString(dsCandIdent.GetColumn(i, "CHK")) == "1")
		{
			dsCandIdent.DeleteRow(i);
		}
	}
	fcSave();

}

//후보생 반편성 삭제
function btDel_OnClick(obj)
{
	var nRecordCount = dsClsOrgan.RowCount();
	//선택한 후보생 추가
	for (var i = 0; i < nRecordCount; i++)
	{
		if (ToString(dsClsOrgan.GetColumn(i, "CHK")) == "1")
		{
			nAddRow = dsCandIdent.AddRow();
			dsCandIdent.SetColumn(nAddRow, "RACER_PERIO_NO", dsClsOrgan.GetColumn(i, "RACER_PERIO_NO"));		
			dsCandIdent.SetColumn(nAddRow, "NM_KOR", dsClsOrgan.GetColumn(i, "NM_KOR"));
			dsCandIdent.SetColumn(nAddRow, "CAND_NO", dsClsOrgan.GetColumn(i, "CAND_NO"));
		}
	}

	//추가한 후보생 그리드에서 삭제 
	for (var i = nRecordCount; i >= 0; i--)
	{
		if (ToString(dsClsOrgan.GetColumn(i, "CHK")) == "1")
		{
			dsClsOrgan.DeleteRow(i);
		}
	}	
	fcSave();
}


