/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'F'+sInpt+'FTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);
	tabMain.TabIndex = 0;
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsWrtExam_Date|dsWrtExam_Detail";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}
/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";	     

    fcInit();

    if (!fcSearchCheck(obj)) return;

    sServiceName = "d06000015_new-service:searchWrtExam";
    sInDataSet   = "";
    sOutDataSet  = "dsDateList=dsDateList "
    	         + "dsWrtExam_CandList=dsWrtExam_CandList "
    	         + "dsWrtExam_Date=dsWrtExam_Date "
    	         + "dsWrtExam_Round=dsWrtExam_Round "
    	         + "dsWrtExam_Total=dsWrtExam_Total ";

    fcd_AddParam("RACER_PERIO_NO", Trim(cbPerioNo.Value));

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function fcSearchCheck(obj) {
	if (!fnc_IsMandatory(cbPerioNo, "기수")) return false;
	return true;
}


/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsDateList.ClearData();
	dsWrtExam_Detail.ClearData();
	dsWrtExam_CandList.ClearData();
	dsWrtExam_Date.ClearData();
	dsWrtExam_Round.ClearData();
    dsWrtExam_Total.ClearData();
}



/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	if (!fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

	// 일자별 학과평가
    sServiceName  = "d06000015_new-service:saveWrtnEstmDetail";
    sInDataSet    = "dsDateList=dsDateList:u "
				  +	"dsWrtExam_Detail=dsWrtExam_Detail ";
    sOutDataSet   = "";

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
    if (!fnc_DatasetChangeCheck(dsDateList) && !fnc_DatasetChangeCheck(dsWrtExam_Detail)) 
    {
        //저장 할 자료가 없음
        fnc_Message(SNIS_COM_1006, "N");	
        return;
    }

	if (!fnc_IsMandatory(cbPerioNo, "기수")) return false;

	return true;
}


/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(){
		var logImgUrl = GBL_REPORT_URL+"log.jpg";
		var sSubUrl   = "";                       		//레포트 파일이 있는 서브디렉토리
		var sRptNm    = "d06000015.jrf";          		//레포트 파일명
		var sDataSet  = "dsWrtExam_Round=dsWrtExam_Round";  	//"UBI 쿼리명, MIP Dataset명"
		var sArg      = " PerioNo=" + cbPerioNo.Text;	

		var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)

		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}	


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {

	if(tabMain.TabIndex == 0){      // 일자별 학과평가
		var sExcelList = "tabMain.tab1.grdWrtnEstm1:학과일자|tabMain.tab1.grdWrtnEstm2:일자별세부기록";
		fcd_OpenExcelListExport(sExcelList);
	}
	else if (tabMain.TabIndex == 1){   // 차수별 학과집계
		fnc_OpenExcelExport("tabMain.tab4.grdWrtnEstm7");
	}
}


