/*-------------------------------------------------------------------------------------------------+
>>  그리드에서 팝업 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnExpandEdit(objGrd, nRow, nCell, strVal, nPivotIndex){
	var sColIdx     = objGrd.GetCellText("HEAD", nRow, nCell);  //해당 HEAD명 
	switch (sColIdx){
		case "일자" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;

		default :
			break;
	}
}



/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
		fcSearch();
        return;
	}

	// 조회버튼 클릭 후
	if ( sServiceName == "d06000015_new-service:saveWrtnEstm" ) {
		if(dsDateList.GetRowCount() <= 0) {
			fnc_Message("조회내역이 존재하지 않습니다.", "N");
		} else {
			fnc_SetStatus(SNIS_CAN_0003,  dsWrtExam_Detail.GetRowCount());
		}

        return;
	}

    // 저장버튼 클릭 후
    if(sServiceName  == "d06000015_new-service:saveWrtnEstmDetail") {
		fcSearch();
		return;
    }

}


/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리
	if ( obj.GetForm().id == "divBtnRow" ) {
		if ( tabMain.TabIndex == 0 ) {      // 일자별 체력측정
			dsDateList.AddRow();
			dsDateList.SetColumn(dsDateList.currow, "RACER_PERIO_NO", cbPerioNo.Value);
			dsWrtExam_Detail.ClearData();
		}
    }        
}


/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	var nCheck = false;

	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRow" ) {
		if ( tabMain.TabIndex == 0 ) {

			nCheck = fnc_DeleteCheck(dsDateList, "CHK", "1");
			if(nCheck) {
				dsWrtExam_Detail.ClearData();
				dsDateList_OnRowPosChanged(object(dsDateList.ID), "", dsDateList.currow);
			} else return;

		}

		break;
	}
}


function dsDateList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	//동일한 날짜가 존재하는지 체크
	if ( strColumnID == "DT" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "날짜");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
			return;
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "날짜");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
			return;
	    }

	    //날짜 입력 후 차수가 null이면 차수입력 알림창 표출
	    if(fnc_IsNull(obj.Getcolumn(nRow, "ROUND"))) {
			fnc_Message("차수를 입력하시기 바랍니다.", "N");
			return;
		}

        dsDateList_OnRowPosChanged(obj,null,nRow);
    }

    // 일자가 null이 아니면 일자별 세부기록 조회
   	if ( strColumnID == "ROUND" ) {
		if(!fnc_IsNull(obj.Getcolumn(nRow, "DT"))) {
			dsDateList_OnRowPosChanged(obj,null,nRow);
		}
   	}
}


function dsDateList_OnRowPosChanged(obj,nOldRow,nRow)
{		 
    if (fnc_IsNull(obj.GetColumn(nRow, "DT"))) {
		dsWrtExam_Detail.ClearData();
		return;
    }

	sServiceName = "d06000015_new-service:searchWrtExamDetail";
	sInDataSet   = "";
	sOutDataSet  = " dsWrtExam_Detail=dsWrtExam_Detail ";

	fcd_AddParam("RACER_PERIO_NO", obj.GetColumn(nRow, "RACER_PERIO_NO"));
	fcd_AddParam("ROUND"         , obj.GetColumn(nRow, "ROUND"));
	fcd_AddParam("DT"  		     , obj.GetColumn(nRow, "DT"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function dsWrtExam_CandList_OnRowPosChanged(obj,nOldRow,nRow)
{
    if (fnc_IsNull(obj.GetColumn(nRow, "CAND_NO")) ) return;

	sServiceName = "d06000015_new-service:searchWrtExamDate";
	sInDataSet   = "";
	sOutDataSet  = " dsWrtExam_Date=dsWrtExam_Date ";

	fcd_AddParam("RACER_PERIO_NO", obj.GetColumn(nRow, "RACER_PERIO_NO"));
	fcd_AddParam("CAND_NO"   	 , obj.GetColumn(nRow, "CAND_NO"       ));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
