/*-------------------------------------------------------------------------------------------------+
>>  ÀúÀå ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//º¯°æµÈ ÀÚ·á°¡ ÀÖ´ÂÁö Ã¼Å©

	// ÇÊ¼öÇ×¸ñÃ¼Å©
	var oDataSet 			= dsStrtMesurCd1;
	var sMandatoryColID   	= null;
	var sMandatoryColName 	= null;
	var oGrid             	= null;

	if(tabMain.TabIndex == 0){   //Æò°¡Ç×¸ñ
		if ( !fnc_DatasetChangeCheck(dsStrtMesurCd1) ) {
			//ÀúÀå ÇÒ ÀÚ·á°¡ ¾øÀ½
			fnc_Message(SNIS_COM_1006, "N");	
			return;
		}		

		// ÇÊ¼öÇ×¸ñÃ¼Å©
		oDataSet 			= dsStrtMesurCd1;	
		sMandatoryColID   	= "ITEM_CD|SEX_GBN|SORT_ORD";
		sMandatoryColName 	= "Æò°¡Ç×¸ñ|¼ºº°±¸ºÐ|Á¤·Ä¼ø¼­";
		oGrid             	= tabMain.tab1.grdStrtMesurCd1;
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}
	}else if(tabMain.TabIndex == 1){ //±âÁØÇ¥µî·Ï
		if ( !fnc_DatasetChangeCheck(dsStrtMesurBas2) ) {
			//ÀúÀå ÇÒ ÀÚ·á°¡ ¾øÀ½
			fnc_Message(SNIS_COM_1006, "N");	
			return;
		}	
	}else if(tabMain.TabIndex == 2){ //Â÷¼öº°Ã¼·ÂÃøÁ¤
		if ( !fnc_DatasetChangeCheck(dsStrtMesurRound5) ) 
		{
			//ÀúÀå ÇÒ ÀÚ·á°¡ ¾øÀ½
			fnc_Message(SNIS_COM_1006, "N");	
			return;
		}

		if(length(tabMain.tab5.CalDt.Value) <= 0){	
			//ÀÏÀÚ ÇÊ¼ö ÀúÀå
			fnc_Message(SNIS_CAN_A003, "N");
			return;
		}

		// ÇÊ¼öÇ×¸ñÃ¼Å©
		oDataSet 			= dsStrtMesurRound5;	
		sMandatoryColID   	= "ITEM01_REC|ITEM02_REC|ITEM04_REC|ITEM05_REC|ITEM06_REC|ITEM07_REC|ITEM08_REC";
		sMandatoryColName 	= "¾Ç·Â|À­¸öÀÏÀ¸Å°±â|Àü½Å¹ÝÀÀ|¾É¾ÆÀ­¸ö¾ÕÀ¸·Î±ÁÈ÷±â|Á¦ÀÚ¸®³ôÀÌ¶Ù±â|½ºÅÜÅ×½ºÆ®|±Ù°üÀý±â´É°Ë»ç";
		oGrid             	= tabMain.tab5.grdStrtMesurRound5;
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}			

	}

	return true;
}


function fcSave(obj) {

	if ( !fcSaveCheck() ) return;


    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

	if(tabMain.TabIndex == 0){		//Æò°¡Ç×¸ñ	  
		sServiceName  = "d06000016-service:saveStrtMesur";
		sInDataSet    = "dsStrtMesurCd1=dsStrtMesurCd1:u ";	//u:¼öÁ¤, a:¸ðµçµ¥ÀÌÅÍ
		sOutDataSet   = "dsStrtMesurCd1=dsStrtMesurCd1 ";
/*
		for(var i=0; i<dsStrtMesurCd1.rowcount; i++) {   
			dsStrtMesurCd1.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
		}		
*/		
		fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));	
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
	}else if(tabMain.TabIndex == 1){ //±âÁØÇ¥µî·Ï
		sServiceName  = "d06000016-service:saveStrtMesur";
		sInDataSet    = "dsStrtMesurBas2=dsStrtMesurBas2:u ";	//u:¼öÁ¤, a:¸ðµçµ¥ÀÌÅÍ
		sOutDataSet   = "dsStrtMesurBas2=dsStrtMesurBas2 ";

		for(var i=0; i<dsStrtMesurBas2.rowcount; i++) {   
			dsStrtMesurBas2.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
		}
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);			
	}else if(tabMain.TabIndex == 2){ //Â÷¼öº°Ã¼·ÂÃøÁ¤
		sServiceName  = "d06000016-service:saveStrtMesur";
		sInDataSet    = "dsStrtMesurRound5=dsStrtMesurRound5:u ";	//u:¼öÁ¤, a:¸ðµçµ¥ÀÌÅÍ
		sOutDataSet   = "dsStrtMesurRound5=dsStrtMesurRound5 ";

		for(var i=0; i<dsStrtMesurRound5.rowcount; i++) {   
			dsStrtMesurRound5.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
			dsStrtMesurRound5.SetColumn(i, "ROUND", 		 Trim(cbRound.Value));
			dsStrtMesurRound5.SetColumn(i, "DT", 			 Trim(tabMain.tab5.CalDt.Value));
		}
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);		
	}

	fcSearch();
}

