function fcGrdOnExpandEdit(objGrd, nRow, nCell, strVal, nPivotIndex){
	var sColIdx     = objGrd.GetCellText("HEAD", nRow, nCell);  //ÇØ´ç HEAD¸í 
	switch (sColIdx){
		case "ÀÏÀÚ" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			dsStrtMesurDt3_OnRowPosChanged(dsStrtMesurDt3,null,nRow);
			break;
		default :
			break;
	}
}

