/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	switch(sServiceName) {

         //Æò°¡Ç×¸ñ
         case "d06000016-service:searchStrtMesurCdPre" :
			dsStrtMesurCd1.UpdateControl = "false";
			var cnt = dsStrtMesurCd1.GetCount();
			for(var i=0; i<cnt; i++) {
				dsStrtMesurCd1.setRowType(i,"Insert");
			}
			break;  
		 //±âÁØÇ¥Á¶È¸
         case "d06000016-service:searchStrtMesurBasPre" :
			dsStrtMesurBas2.UpdateControl = "false";
			var cnt = dsStrtMesurBas2.GetCount();
			for(var i=0; i<cnt; i++) {
				dsStrtMesurBas2.setRowType(i,"Insert");
			}
            break; 
         //ÀÏÀÚº°Ã¼·ÂÃøÁ¤Á¶È¸     
         case "d06000016-service:searchStrtMesurDt3" :
			  dsStrtMesurDt3.AddColumn("CHK");
			  break; 
         case "d06000016-service:searchStrtMesurDt3_1" :
            break; 
        // Â÷¼öº°Ã¼·ÂÃøÁ¤ Á¶È¸ ÄÝ¹é
        case "d06000016-service:searchStrtMesurRound5" :
            fcSearchBas();
            break;

		case "d06000016-service:saveStrtMesur" :
			fcSearch();
			break;

		case "d06000016-service:deleteStrtMesurDt3_1" :
			fcSearch();
			break;
		case "d06000016-service:searchPerioNo" :
			fcSearch();
			break;
		default :
			break;
	}

    //¿À·ù°¡ ¾øÀÌ Á¤»ó Ã³¸®µÇ¾ú´Ù¸é...
	fcPostProcess(sServiceName);
}

