function fcAddRow(obj) {

	//DataSetÀÇ Header Á¤º¸°¡ ÀÖÀ» ¶§  Add Ã³¸®
	if ( obj.GetForm().id == "divBtnRow" ) {

		if(tabMain.TabIndex == 0){ 		//Æò°¡Ç×¸ñ
			dsStrtMesurCd1.AddRow();
			dsStrtMesurCd1.SetColumn(dsStrtMesurCd1.currow, "RACER_PERIO_NO", cbPerioNo.Value);
			dsStrtMesurCd1.SetColumn(dsStrtMesurCd1.currow, "CHK", "0");
		}
    }        
}

/*-------------------------------------------------------------------------------------------------+
>>  Çà»èÁ¦ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSetÀÇ Header Á¤º¸°¡ ÀÖÀ» ¶§  Add Ã³¸® 	
	if ( obj.GetForm().id == "divBtnRow" ) {
		if(tabMain.TabIndex == 0){
			if ( !fnc_DeleteCheck(dsStrtMesurCd1, "CHK", "1") ) return;
			     tabMain.tab1.grdStrtMesurCd1.SetFocus(); //Record »èÁ¦ ÈÄ Grid·Î Focus
		}

		break;
	}
}

//½ÃÀÛ½Ã¿¡ °Ë»öÁ¶°Ç Ã³¸®
function fcLoadEnable(){
	if(tabMain.TabIndex == 0){
		cbRound.Enable 			= false;
	}else if(tabMain.TabIndex == 1){
		cbRound.Enable 			= false;		
	}else if(tabMain.TabIndex == 2){
		cbRound.Enable 			= true;	
	}
}	

function tabMain_OnChanged(obj,nOldIndex,nNewindex)
{
	fcLoadEnable();
}

//ÀÏÀÚº°Áý°Ô »ó¼¼Á¶È¸
function tabMain_tab4_grdStrtMesurDtTot4_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    var sServiceName 	= "";
    var sInDataSet   	= "";
    var sOutDataSet  	= "";

	for(var i=0; i<dsStrtMesurDtTot4.rowcount; i++) {
		if(i == nRow){
			sRacerPerioNo 	= dsStrtMesurDtTot4.GetColumn(i, "RACER_PERIO_NO");
			sCandNo			= dsStrtMesurDtTot4.GetColumn(i, "CAND_NO");
			break;
		}
	}		

	sServiceName = "d06000016-service:searchStrtMesurDtTot4_1";
	sInDataSet   = "";
	sOutDataSet  = " dsStrtMesurDtTot4_1=dsStrtMesurDtTot4_1 ";	
	fcd_AddParam("racerPerioNo", sRacerPerioNo);
	fcd_AddParam("candNo", sCandNo);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
