/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {
		if(tabMain.TabIndex == 0) {
			dsMotorItem.ClearData();	
			dsMotorItem_1.ClearData();
			dsMotorItem_2.ClearData();
		}
		else if(tabMain.TabIndex == 1) {
			dsTab1Main.ClearData();	
			dsTab1Sub1.ClearData();	
			dsTab1Sub2.ClearData();
			dsTab1Sub3.ClearData();	
		}
	}    
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {
//    edSCfrntCd.Text = Trim(edSCfrntCd.Text);
	fcInit(obj);	

	if(tabMain.TabIndex == 0){	    //  모터분해조립 평가 항목	

		if(length(cbPerioNo.Value) < 1){	
			//기수 필수 저장
			fnc_Message(SNIS_CAN_E001, "N", "기수를 ");
			return;
		}		
	}

	if(tabMain.TabIndex == 1){
		if(length(cbPerioNo.Value) < 1){	
			//기수 필수 저장
			fnc_Message(SNIS_CAN_E001, "N", "기수를 ");
			return;
		}		
	}

	return true;
}

function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";	     


	//if(tabMain.TabIndex == 0) {     //  모터분해조립 평가 항목 조회 
		sServiceName = "d06000017-service:searchMotorItem";
		sInDataSet   = "";
		sOutDataSet  = " dsMotorItem=dsMotorItem";	
		fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));		
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);

		sServiceName = "d06000017-service:searchMotorItem_1";
		sInDataSet   = "";
		sOutDataSet  = " dsMotorItem_1=dsMotorItem_1";		
		fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));		
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);		


		sServiceName = "d06000017-service:searchMotorItem_2";
		sInDataSet   = "";
		sOutDataSet  = " dsMotorItem_2=dsMotorItem_2";		
		fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));		
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);		


	//} else if(tabMain.TabIndex == 1) {    // 모터분해조립 평가표 조회
        sServiceName = "d06000017-service:searchEstmMotorMake";
		sInDataSet   = "";
		sOutDataSet  = " dsTab1Main=dsTab1Main";	
		fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));		
		fcd_AddParam("roundNo", Trim(cbRound.Value));
		fcd_AddParam("estmId",  Trim(gdsUser.getColumn(0,"USER_ID")));

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);

		//차수별 평가결과 조회
		sServiceName = "d06000017-service:searchMotorEstmItemSum";
