		sOutDataSet  = " dsOutMotorEstmItemSum=dsOutMotorEstmItemSum dsOutMotorEstmItemSumPrint=dsOutMotorEstmItemSumPrint";

	    fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));
	    fcd_AddParam("round", Trim(cbRound.Value));
