	//}
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크

	// 필수항목체크
	var oDataSet 			= "";
	var sMandatoryColID   	= null;
	var sMandatoryColName 	= null;
	var oGrid             	= null;

	if(tabMain.TabIndex == 0){	    //  모터분해조립 평가 항목	
		if ( !fnc_DatasetChangeCheck(dsMotorItem) 
			&& !fnc_DatasetChangeCheck(dsMotorItem_1) 
			&& !fnc_DatasetChangeCheck(dsMotorItem_2)) 
        {   
            //저장 할 자료가 없음
            fnc_Message(SNIS_COM_1006, "N");	
            return;
        }

		if(length(cbPerioNo.Value) < 1){	
			//기수 필수 저장
			fnc_Message(SNIS_CAN_E001, "N", "기수를 ");
			return;
		}		


	}


	if(tabMain.TabIndex == 1) {
		if ( length(tabMain.tab2.calEstmDate.value) < 1 ) {
			fnc_Message(SNIS_CAN_E001, "N","일자를 ");
			return;
		}

		//alert("111");
		if ( !fnc_DatasetChangeCheck(dsTab1Main) 
			&& !fnc_DatasetChangeCheck(dsTab1Sub1) 
			&& !fnc_DatasetChangeCheck(dsTab1Sub2)) 
        {
            //저장 할 자료가 없음
            fnc_Message(SNIS_COM_1006, "N");	
            return;
        }

		if(length(cbPerioNo.Value) <= 0){	
			//기수 필수 저장
			fnc_Message(SNIS_CAN_E001, "N", "기수를 ");
			return;
		}	

		if(length(tabMain.tab2.calEstmDate.value) <> 8) {
			//alert("일자를 선택하세요.");
			fnc_Message(SNIS_CAN_E001, "N", "일자를 ");
			return;
		}
		if(length(cbRound.Value) < 1){	
			//기수 필수 저장
			fnc_Message(SNIS_CAN_E001, "N", "차수를 ");
			return;
		}		
	}


	return true;
}


function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

	// 모터분해조립 평가 항목
	if(tabMain.TabIndex == 0) { 
		sServiceName  = "d06000017-service:saveMotorItem";
		sInDataSet    = " dsMotorItem=dsMotorItem:a dsMotorItem_1=dsMotorItem_1:a dsMotorItem_2=dsMotorItem_2:a";
		sOutDataSet   = " dsMotorItem=dsMotorItem dsMotorItem_1=dsMotorItem_1 dsMotorItem_2=dsMotorItem_2";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);


	} else if(tabMain.TabIndex == 1) { 

		sServiceName  = "d06000017-service:saveMotorItemEstm";
		sInDataSet    = " dsTab1Main=dsTab1Main:u dsTab1Sub1=dsTab1Sub1:u dsTab1Sub2=dsTab1Sub2:u";
		sOutDataSet   = " dsTab1Main=dsTab1Main dsTab1Sub1=dsTab1Sub1 dsTab1Sub2=dsTab1Sub2";

		for(var i=0; i<dsTab1Sub1.rowcount; i++) {   
			 if( dsTab1Sub1.GetRowType(i)=="update" ){
				dsTab1Sub1.SetColumn(i, "ROUND", cbRound.Value);
				dsTab1Sub1.SetColumn(i, "CAND_NO", dsTab1Main.GetColumn(dsTab1Main.currow,"CAND_NO"));
			}
		}

		for(var i=0; i<dsTab1Sub2.rowcount; i++) {   
			if( dsTab1Sub2.GetRowType(i)=="update" ){
				dsTab1Sub2.SetColumn(i, "ROUND", cbRound.Value);
				dsTab1Sub2.SetColumn(i, "CAND_NO", dsTab1Main.GetColumn(dsTab1Main.currow,"CAND_NO"));
			}

		}
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);

	}
}

//기수콤보
function fcPerioNo() {
	var sServiceName = "d06000017-service:searchPerioNo";
    var sInDataSet   = "";
    var sOutDataSet  = " dsPerioNo=dsPerioNo "; 

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}



