function fcToExcel(obj) {

	if(tabMain.TabIndex == 0){      	// 모터분해조립 평가 항목
		var sExcelList = "tabMain.tab1.grdMotorItem:모터분해조립숙련도|tabMain.tab1.grdMotorItem_1:모터분해조립시간";
		fcd_OpenExcelListExport(sExcelList);
	} else if (tabMain.TabIndex == 1){   // 모터분해조립 평가표
		var sExcelList = "tabMain.tab2.gridTab1Main:모터분해조립평가표|tabMain.tab2.grdMotorMakeItem:모터분해조립숙련도|tabMain.tab2.grdMotorItem_1:모터분해조립시간";
		fcd_OpenExcelListExport(sExcelList);
	} else {
		alert("엑셀 기능이 지원되지 않습니다.");
	}
}

