/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    switch(sServiceName) {
		case "d06000017-service:searchPerioNo" :   
			fcsearch();
            break;
		case "d06000017-service:searchMotorItem" :
			if(dsMotorItem.rowcount == 0 && dsMotorItem_1.rowcount == 0 && dsMotorItem_2.rowcount == 0) {
				tabMain.tab1.btnPrevCp.Enable = true;
			}
			else {
				tabMain.tab1.btnPrevCp.Enable = false;
			}
			break;
        case "d06000017-service:saveMotorItem" :
			break;                              

        case "d06000017-service:searchPrevMotorItem" :
			tabMain.tab1.btnPrevCp.Enable = false;
			if(dsPrevMotorItem.rowcount > 0) {
				tabMain.tab1.grdMotorItem.Visible = false;
				for(var i=0; i < dsPrevMotorItem.rowcount; i++) {
					dsMotorItem.AddRow();
					for(var j=0; j < dsPrevMotorItem.colcount; j++) {
						dsMotorItem.SetColumn(dsMotorItem.row,j,dsPrevMotorItem.getColumn(i,j));
					}
					dsMotorItem.SetColumn(dsMotorItem.row,"RACER_PERIO_NO",cbPerioNo.Value);
				}
				tabMain.tab1.grdMotorItem.Visible = true;
			}
			if(dsPrevMotorItem_1.rowcount > 0) {
				tabMain.tab1.grdMotorItem_1.Visible = false;
				for(var i=0; i < dsPrevMotorItem_1.rowcount; i++) {
					dsMotorItem_1.AddRow();
					for(var j=0; j < dsPrevMotorItem_1.colcount; j++) {
						dsMotorItem_1.SetColumn(dsMotorItem_1.row,j,dsPrevMotorItem_1.getColumn(i,j));
					}
					dsMotorItem_1.SetColumn(dsMotorItem_1.row,"RACER_PERIO_NO",cbPerioNo.Value);
				}
				tabMain.tab1.grdMotorItem_1.Visible = true;
			}
			if(dsPrevMotorItem_2.rowcount > 0) {
				tabMain.tab1.grdMotorItem_2.Visible = false;
				for(var i=0; i < dsPrevMotorItem_2.rowcount; i++) {
					dsMotorItem_2.AddRow();
					for(var j=0; j < dsPrevMotorItem_2.colcount; j++) {
						dsMotorItem_2.SetColumn(dsMotorItem_2.row,j,dsPrevMotorItem_2.getColumn(i,j));
					}
					dsMotorItem_2.SetColumn(dsMotorItem_2.row,"RACER_PERIO_NO",cbPerioNo.Value);
				}
				tabMain.tab1.grdMotorItem_2.Visible = true;
			}
			break;
		case "d06000017-service:searchEstmMotorMake" :
			if(dsTab1Main.GetRowCount() > 0) {
				dsTab1Main_OnRowPosChanged("", "", 0);
			}	
		break;
		case "d06000017-service:saveMotorItemEstm" :
			fcsearch();
		break;
        default :
            break;
    }
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

