}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리
	if ( obj.GetForm().id == "divBtnRow" ) {

		if(tabMain.TabIndex == 0){      // 일자별 체력측정
			dsMotorItem.AddRow();
			dsMotorItem.SetColumn(dsMotorItem.row, "RACER_PERIO_NO", cbPerioNo.Value);
		}else if(tabMain.TabIndex == 2){    // 

		}
    }

    if ( obj.GetForm().id == "divBtnRow_1" ) {
		if(tabMain.TabIndex == 0){
			dsMotorItem_1.AddRow();
			dsMotorItem_1.SetColumn(dsMotorItem_1.row, "RACER_PERIO_NO", cbPerioNo.Value);
		}
	}

	if ( obj.GetForm().id == "divBtnRow_2" ) {
		if(tabMain.TabIndex == 0){
			dsMotorItem_2.AddRow();
			dsMotorItem_2.SetColumn(dsMotorItem_2.row, "RACER_PERIO_NO", cbPerioNo.Value);
		}
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRow" ) {
		if(tabMain.TabIndex == 0){
			if ( !fnc_DeleteCheck(dsMotorItem, "CHK", "1") ) return;
			tabMain.tab1.grdMotorItem.SetFocus(); //Record 삭제 후 Grid로 Focus
		}
		else if(tabMain.TabIndex == 2){

		}
		fcInit();
		break;
	}

	if ( obj.GetForm().id == "divBtnRow_1" ) {
		if(tabMain.TabIndex == 0){
			if ( !fnc_DeleteCheck(dsMotorItem_1, "CHK", "1") ) return;
			tabMain.tab1.grdMotorItem_1.SetFocus(); //Record 삭제 후 Grid로 Focus
		}
	}

	if ( obj.GetForm().id == "divBtnRow_2" ) {
		if ( !fnc_DeleteCheck(dsMotorItem_2, "CHK", "1") ) return;
			tabMain.tab1.grdMotorItem_2.SetFocus(); //Record 삭제 후 Grid로 Focus
	}
}

