function dsTab1Main_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	var maindt = "";
	for(var i=0; i<dsTab1Main.count; i++){
		maindt = dsTab1Main.GetColumn(i,"DT");
		if ( maindt.length = 8 ){ 
			break;
		}
	}
	tabMain.tab2.calEstmDate.value = maindt;
}

