function dsTab1Main_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "ASSIGN_SCR") {
		if (varNewValue < 60) {
			dsTab1Main.SetColumn(nRow, "DCSN", "003");
		}
		else {
			dsTab1Main.SetColumn(nRow, "DCSN", "001");
		}
	}

	if (strColumnID == "ASSEM_SCR" ) {
		//alert("11111");
		var mainrow = dsTab1Main.currow;
		var mainas_scr = dsTab1Main.GetColumn(mainrow,"ASSEM_SCR");
		var mainastm_scr = dsTab1Main.GetColumn(mainrow,"ASSEM_TIME_SCR");

		dsTab1Main.SetColumn(mainrow,"ASSIGN_SCR", toNumber(mainas_scr) + toNumber(mainastm_scr));

	}//if


	if ( strColumnID == "ASSEM_TIME_SCR") {
		var mainrow = dsTab1Main.currow;
		var mainas_scr = dsTab1Main.GetColumn(mainrow,"ASSEM_SCR");
		var mainastm_scr = dsTab1Main.GetColumn(mainrow,"ASSEM_TIME_SCR");
		dsTab1Main.SetColumn(mainrow,"ASSIGN_SCR", toNumber(mainas_scr) + toNumber(mainastm_scr));
	}//if
}


function ifexist(objid, newval)
{
	var is_exist=false;
	var compare;


   	if (objid == "dsMotorItem") {
		for(var i=0;i<dsMotorItem.count;i++){
			compare=(dsMotorItem.GetColumn(i,"ESTM_ITEM_DETL_CD"));
			if (compare == newval){
				is_exist=true;
				alert("중복되는 건이 있습니다.");
				if ( dsMotorItem.GetRowType(nRow) == "insert" ) {
					dsMotorItem.DeleteRow(nRow);
				}	

				return is_exist;
			}
		}
		return is_exist;
	}


}

function dsMotorItem_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	if (strColumnID == "ESTM_ITEM_DETL_CD") { // if 1

		for (var i=0;i<dsMotorItem.count;i++) { //for

			if ( varNewValue == dsMotorItem.GetColumn(i,"ESTM_ITEM_DETL_CD") ) { // if2


				if ( dsMotorItem.GetRowType(nRow) == "insert" ) {
					dsMotorItem.DeleteRow(nRow);
				}	
				alert("중복되는 건이 있습니다.");
				return false;

				break;

			} //if 2

		}//for

	}//if 1

}

