function tabMain_tab2_grdMotorMakeItem_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	if (nCell==3) {
		var scr = dsTab1Sub1.GetColumn(nRow, "VERY_SUPER_ASSIGN_SCR");
		dsTab1sub1.SetColumn(nRow, "ESTM_SCR", scr);
	}
	if (nCell==4) {
		var scr = dsTab1Sub1.GetColumn(nRow, "SUPER_ASSIGN_SCR");
		dsTab1sub1.SetColumn(nRow, "ESTM_SCR", scr);
	}
	if (nCell==5) {
		var scr = dsTab1Sub1.GetColumn(nRow, "NORM_ASSIGN_SCR");
		dsTab1sub1.SetColumn(nRow, "ESTM_SCR", scr);
	}
	if (nCell==6) {
		var scr = dsTab1Sub1.GetColumn(nRow, "INSUF_ASSIGN_SCR");
		dsTab1sub1.SetColumn(nRow, "ESTM_SCR", scr);
	}
	if (nCell==7) {
		var scr = dsTab1Sub1.GetColumn(nRow, "VERY_INSUF_ASSIGN_SCR");
		dsTab1sub1.SetColumn(nRow, "ESTM_SCR", scr);
	}
}


function tabMain_tab2_grdMotorItem_1_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell==2) {
		var scr = dsTab1Sub2.GetColumn(nRow, "ASSIGN_SCR_LIMIT");
		//alert(ncell +"   " +scr);


		//dsTab1Sub2.fireEvent = false;
		for (var i=0; i<dsTab1Sub2.GetRowCount();i++) {
			if (i != nRow ) {
				dsTab1Sub2.SetColumn(i,"ESTM_SCR",null);
				dsTab1Sub2.SetColumn(i,"ESTM_REC",null);
			}
		}
		//dsTab1Sub2.fireEvent = true;
		dsTab1sub2.SetColumn(nRow, "ESTM_SCR", scr);


	}
}

