function tabMain_tab1_btnPrevCp_OnClick(obj)
{
	sServiceName = "d06000017-service:searchPrevMotorItem";
	sInDataSet   = "";
	sOutDataSet  = " dsPrevMotorItem=dsPrevMotorItem";
	sOutDataSet += " dsPrevMotorItem_1=dsPrevMotorItem_1";
	sOutDataSet += " dsPrevMotorItem_2=dsPrevMotorItem_2";
	fcd_AddParam("racerPerioNo", lpad(parseInt(Trim(cbPerioNo.Value)) - 1,"0",2));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);

}
function fcPrint(){
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "";          //레포트 파일명
	var sDataSet  = "";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "";
	if (tabMain.TabIndex == 1){
			 sRptNm    = "d06000017_1.jrf";          //레포트 파일명
			 sDataSet  = " dsTab1Main=dsTab1Main ";    //"UBI 쿼리명, MIP Dataset명"
			 sArg      = "PerioNo=" + cbPerioNo.Text+ " Round="+cbRound.Text ;
			 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
			 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}else if (tabMain.TabIndex == 2){ 
			 sRptNm    = "d06000017_2.jrf";          //레포트 파일명
			 sDataSet  = " dsOutMotorEstmItemSumPrint=dsOutMotorEstmItemSumPrint ";    //"UBI 쿼리명, MIP Dataset명"
			 sArg      = "PerioNo=" + cbPerioNo.Text+ " Round="+cbRound.Text ;;
			 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
			 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}
}


