/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'T'+sInpt+'FTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";
	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);
	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);	
	//공통코드 조회
	fcd_GetComboDs(dsInComCd);	
	// 화면 초기화
	fcInit();
	//기수쿼리
	fcPerioNo();
	cbPerioNo.Index 	= 0;
	cbSearchRound.Index = 0;

	divBtnLst.btnCancel.Visible  = false;  //초기화
	divBtnLst.btnSave.Visible    = false;  //저장
	divBtnLst.btnToExcel.Enable = false;  //엑셀
}

