function fcToExcel(obj) {

	ExportObject = makeExcel();
	ExportObject.ExportType		= "Excel";
	ExportObject.ExportFileName	= "c:\\a.xls";
	ExportObject.MakeEmptyFileWhenNotExist = true;		
	ExportObject.Export();
	ExportObject.Close();
	ExportObject = null;		
}

function makeExcel() {
	ExportObject1 = CreateExportObject();

	dsTitle1.addRow();
	dsTitle2.addRow();
	dsTitle3.addRow();
	dsTitle4.addRow();
	dsTitle5.addRow();
	dsTitle6.addRow();
	var titleHeader = "제"+Trim(cbPerioNo.Value)+"기 후보생 " + Trim(cbSearchRound.Value) + "차 평가 ";

	/*
		제목을 설정하기 위해서 Grid를 동적으로 생성하고, 각 그리드마다 dataset을 할당
	*/
	ExportObject1.AddExportGrid("신체검사집계표"       + "!" + "A5", tabMain.tab2.grdBInsp, true, false);
	create("grid","grdExcel6",'BindDataSet=dsTitle6');
	grdExcel6.contents = makeGrid(tabMain.tab2.grdBInsp.getColcount());
	ExportObject1.AddExportGrid("신체검사집계표"       + "!" + "A3", grdExcel6, true, false);
	dsTitle6.setColumn(0,"TITLE",titleHeader+"신체검사집계표");

	ExportObject1.AddExportGrid("체력측정집계표" + "!" + "A5", tabMain.tab3.grdStrt, true, false);
	create("grid","grdExcel5",'BindDataSet=dsTitle5');
	grdExcel5.contents = makeGrid(tabMain.tab3.grdStrt.getColcount());
	ExportObject1.AddExportGrid("체력측정집계표"       + "!" + "A3", grdExcel5, true, false);
	dsTitle5.setColumn(0,"TITLE",titleHeader+"체력측정집계표");

	ExportObject1.AddExportGrid("모터정비실기집계표" + "!" + "A5", tabMain.tab6.grdMotor, true, false);
	create("grid","grdExcel4",'BindDataSet=dsTitle4');
	grdExcel4.contents = makeGrid(tabMain.tab6.grdMotor.getColcount());
	ExportObject1.AddExportGrid("모터정비실기집계표"       + "!" + "A3", grdExcel2, true, false);
	dsTitle4.setColumn(0,"TITLE",titleHeader+"모터정비실기집계표");

	ExportObject1.AddExportGrid("조정실기집계표" + "!" + "A5", tabMain.tab5.grdPilot, true, false);
	create("grid","grdExcel3",'BindDataSet=dsTitle3');
	grdExcel3.contents = makeGrid(tabMain.tab5.grdPilot.getColcount());
	ExportObject1.AddExportGrid("조정실기집계표"       + "!" + "A3", grdExcel3, true, false);
	dsTitle3.setColumn(0,"TITLE",titleHeader+"조정실기집계표");

	ExportObject1.AddExportGrid("학과평가집계표" + "!" + "A5", tabMain.tab4.grdWrtn, true, false);
	create("grid","grdExcel2",'BindDataSet=dsTitle2');
	grdExcel2.contents = makeGrid(tabMain.tab4.grdWrtn.getColcount());
	ExportObject1.AddExportGrid("학과평가집계표"       + "!" + "A3", grdExcel4, true, false);
	dsTitle2.setColumn(0,"TITLE",titleHeader+"학과평가집계표");

	ExportObject1.AddExportGrid("총괄표"         + "!" + "A5", tabMain.tab1.grdEstmItemTot, true, false);
	create("grid","grdExcel1",'BindDataSet=dsTitle1');
	grdExcel1.contents = makeGrid(tabMain.tab1.grdEstmItemTot.getColcount());
	ExportObject1.AddExportGrid("총괄표"       + "!" + "A3", grdExcel1, true, false);
	dsTitle1.setColumn(0,"TITLE",titleHeader+"총괄표");

	return ExportObject1;
}

function makeGrid(colCnt) {
	var strContent = "";
	strContent  = '<contents>';
	strContent += '<format id="Default">';
	strContent += '	<columns>';
	for(var i=0; i<colCnt; i++) {
		strContent += '<col width="80"/>';
	}
	strContent += '	</columns>';
	strContent += '		<body>';
	strContent += '			<cell align="center" col="0" colid="TITLE" colspan="'+colCnt+'" display="text"/>';
	strContent += '		</body>';
	strContent += '</format>';
	strContent += '</contents>';
	return strContent;
}


