function btnCfm_OnClick(obj)
{
	if ( !fcSearchCheck(obj) ) return;

	dsCfm.SetColumn(0,"CFM_YN","Y");

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    sServiceName  = "d06000021-service:saveCfm";

    sInDataSet     = " dsCfm=dsCfm " ;
    sOutDataSet    = " dsCfm=dsCfm " ;

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

function btnCfmCls_OnClick(obj)
{
	if ( !fcSearchCheck(obj) ) return;

	dsCfm.SetColumn(0,"CFM_YN","N");

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    sServiceName  = "d06000021-service:saveCfm";

    sInDataSet     = " dsCfm=dsCfm " ;
    sOutDataSet    = " dsCfm=dsCfm " ;

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");	
}

function btnMake_OnClick(obj)
{
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

	if(confirm("집계하시면 자료가 새로 생성됩니다.\n집계하시겠습니까?")) {
		sServiceName  = "d06000021-service:saveEstmItemTot";

		sInDataSet     = "" ;
		sOutDataSet    = "" ;

		fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));
		fcd_AddParam("round", Trim(cbSearchRound.Value));	

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");	
    }
}

function fcElecPayment() {

	ExportObject = makeExcel();
	var pe = cbPerioNo.value;
	var ro = cbSearchRound.value;
	var tcnt = dsEstmItemTot.GetCount(); //평가대상자

	dsEstmItemTot.Filter("GENDER='남'");	//남자
	var mancnt = dsEstmItemTot.GetCount();
	dsEstmItemTot.unFilter();

	dsEstmItemTot.Filter("GENDER='여'");	//여자
	var wancnt = dsEstmItemTot.GetCount();
	dsEstmItemTot.unFilter();

	dsEstmItemTot.Filter("TOT_RSLT='001'");	//합격
	var icnt = dsEstmItemTot.GetCount();
	dsEstmItemTot.unFilter();

	dsEstmItemTot.Filter("TOT_RSLT='002'");	//경고
	var wcnt = dsEstmItemTot.GetCount();
	dsEstmItemTot.unFilter();

	dsEstmItemTot.Filter("TOT_RSLT='003'");	//퇴소
	var ocnt = dsEstmItemTot.GetCount();
	dsEstmItemTot.unFilter();

	var tavg = round(dsEstmItemTot.Avg("TOT_AVG_SCR"),1);	//전체평균
	var wavg = round(dsEstmItemTot.Avg("WRTN_AVG_SCR"),1);	//학과실험
	var pavg = round(dsEstmItemTot.Avg("PILOT_AVG_SCR"),1);	//조종실기
	var mavg = round(dsEstmItemTot.Avg("MOTOR_AVG_SCR"),1);	//정비실기
	var savg = round(dsEstmItemTot.Avg("STRT_AVG_SCR"),1);	//체력측정

	var vTitle = "제"+pe+"기 후보생 "+ro+"차 평가 성적결과표";

