function makeGrid(colCnt) {
	var strContent = "";
	strContent  = '<contents>';
	strContent += '<format id="Default">';
	strContent += '	<columns>';
	for(var i=0; i<colCnt; i++) {
		strContent += '<col width="80"/>';
	}
	strContent += '	</columns>';
	strContent += '		<body>';
	strContent += '			<cell align="center" col="0" colid="TITLE" colspan="'+colCnt+'" display="text"/>';
	strContent += '		</body>';
	strContent += '</format>';
	strContent += '</contents>';
	return strContent;
}


