>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	var sRound1Dt = "";
	var sRound2Dt = "";
	var sRound3Dt = "";
	var sRound4Dt = "";

	switch(sServiceName) {
		case "d06000021-service:searchPerioNo" :
			fcSearch();
			break;
		case "d06000021-service:searchEstmItemTot" :
			if((dsCfm.GetColumn(0,"CFM_YN")) == "Y"){	//È®Á¤
				btnCfm.Enable = false;
				btnCfmCls.Enable = true;	
				btnMake.Enable = false;
				divBtnLst.btnToExcel.Enable = true;
//				divBtnLst.btnElec.Enable = true;
            }
            else if((dsCfm.GetColumn(0,"CFM_YN")) == "N"){//¹ÌÈ®Á¤
				btnCfm.Enable = true;
                btnCfmCls.Enable = false;
                btnMake.Enable = true;
                divBtnLst.btnToExcel.Enable = false;
//                divBtnLst.btnElec.Enable = false;
            } else{		//µ¥ÀÌÅÍ°¡ ¾ø´Â °æ¿ì
            	btnCfm.Enable = false;
                btnCfmCls.Enable = false;
                btnMake.Enable = true;
                divBtnLst.btnToExcel.Enable = false;
//                divBtnLst.btnElec.Enable = false;
            }
			break;
		case "d06000021-service:saveCfm" :
			fcSearch();
			break;			
		case "d06000021-service:saveEstmItemTot" :
			fcSearch();
			break;			
		default :
			break;  
	}    

    //¿À·ù°¡ ¾øÀÌ Á¤»ó Ã³¸®µÇ¾ú´Ù¸é...
	fcPostProcess(sServiceName);
}

