var sFormId       = 'D06000025';
var sBUTTONLIST   = 'TTFTFT';                          //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트

/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'F'+sInpt+'FTTT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	// 화면 초기화
	fcInit();

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsMesurItem";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}
/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsMesurItem.ClearData();
}

function fcEnd(obj) {
	//화면 종료
	Close();	
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        fcSearch();
        return;
	}

	if ( sServiceName == "d06000025-service:saveMesurItem" ) {
        fcSearch();
        return;
	}
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}


function fcSearchCheck(obj) {

	return true;
}


function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";	     

	//
	sServiceName = "d06000025-service:searchMesurItem";
	sInDataSet   = "";
	sOutDataSet  = " dsMesurItem=dsMesurItem ";	
	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));
	fcd_AddParam("round",        Trim(cbRound.Value));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false); 

}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck() {

	//변경된 자료가 있는지 체크
    if ( !fnc_DatasetChangeCheck(dsMesurItem)) 
    {
        //저장 할 자료가 없음
        fnc_Message(SNIS_COM_1006, "N");	
        return;
    }
	// 시간배정표 과목 중복입력 체크
	if(fnc_IsColumnDuplicate(dsMesurItem, "ITEM")) {
		fnc_Message(SNIS_CAN_0009, "N", "종목이");	
		return false;    
	}

	return true;
}


function fcSave(obj) {

   if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
	sServiceName  = "d06000025-service:saveMesurItem";
	sInDataSet    = "dsMesurItem=dsMesurItem:u ";
	sOutDataSet   = "dsMesurItem=dsMesurItem ";
	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));
	fcd_AddParam("round",        Trim(cbRound.Value));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리
	if ( obj.GetForm().id == "divBtnRow" ) {
		dsMesurItem.AddRow();
		dsMesurItem.SetColumn(dsMesurItem.currow, "RACER_PERIO_NO", cbPerioNo.Value);
		dsMesurItem.SetColumn(dsMesurItem.currow, "ROUND", cbRound.Value);
    }        
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRow" ) {
		if ( !fnc_DeleteCheck(dsMesurItem, "CHK", "1") ) return;
		break;
	}
}
