/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        fcSearch();
        return;
	}

	if ( sServiceName == "d06000026-service:saveBodyInsp" ) {
        //fcSearch();
        return;
	}

	if ( sServiceName == "d06000026-service:searchBodyInspDt" ) {
		if(dsBodyInspDt.GetRowCount() < 1) {
			dsBodyInspDt.AddRow();
		}
        return;
	}
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}


function fcSearchCheck(obj) {

	return true;
}


function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";	     

	//
	sServiceName = "d06000026-service:searchBodyInsp";
	sInDataSet   = "";
	sOutDataSet  = " dsBodyInsp=dsBodyInsp ";	
	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));
	fcd_AddParam("round",        Trim(cbRound.Value));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false); 

	//평가표 평가일 조회
	sServiceName = "d06000026-service:searchBodyInspDt";
	sInDataSet   = "";
	sOutDataSet  = " dsBodyInspDt=dsBodyInspDt ";
	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));
	fcd_AddParam("round", Trim(cbRound.Value));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);	

}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck() {

	if(length(tabMain.tab2.CalInputDt.Value) <= 0){	 //일자 체크
		//일자는 필수 저장
		fnc_Message(SNIS_CAN_A003, "N");
		return;
	}

	var iDataCheck = 0;
	if(fnc_DatasetChangeCheck(dsBodyInsp) > 0){   //신체검사 세부데이터
			iDataCheck++;
	}
	if(fnc_DatasetChangeCheck(dsBodyInspDt) > 0){ //신체검사일
			iDataCheck++;
	}

	//변경된 자료가 있는지 체크
    if ( iDataCheck==0) 
    {
        //저장 할 자료가 없음
        fnc_Message(SNIS_COM_1006, "N");	
        return;
    }
	return true;
}


function fcSave(obj) {

   if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
	sServiceName  = "d06000026-service:saveBodyInsp";
	sInDataSet    = "dsBodyInsp=dsBodyInsp:u ";
	sOutDataSet   = "dsBodyInsp=dsBodyInsp ";
	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));
	fcd_AddParam("round",        Trim(cbRound.Value));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

	sServiceName  = "d06000026-service:saveBodyInspDt";
	sInDataSet    = "dsBodyInspDt=dsBodyInspDt:u ";
	sOutDataSet   = "dsBodyInspDt=dsBodyInspDt ";
	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));
	fcd_AddParam("round",        Trim(cbRound.Value));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

}
