/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);
	//공통코드 조회
	fcd_GetComboDs(dsInComCd);	
	// 화면 초기화
	//fcInit();
}


/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutCand.ClearData();

    for ( var i = 1; i <= toInteger(dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT")); i++ ) {
        for ( var j = 1; j <= 4; j++ ) {
            var sDatasetNm = "dsOutCandRaceDetl_"+i+"R_"+j+"T";
            object(sDatasetNm).ClearData();
        }
    }
}

function fcToExcel(obj) {
	if(tbMain.TabIndex == 0) {
		fnc_OpenExcelExport("tbMain.tbCand.grdCandList");
	} else if(tbMain.TabIndex == 1) {
		fnc_Message("해당 Tab에서는 엑셀을 열 수 없습니다.", "N");
	} else if(tbMain.TabIndex == 2) {
		fnc_Message("해당 Tab에서는 엑셀을 열 수 없습니다.", "N");
	} else if(tbMain.TabIndex == 3) {
		fnc_OpenExcelExport("tbMain.tbRacePlan.grdRacePlan");
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCandRaceCheck(obj) {
	//fcInit(obj);

	return true;
}

//조회버튼, 화면 로드시 default로 불려진다.
function fcSearchCandRace(obj) {
    if ( !fcSearchCandRaceCheck(obj) ) return;

    var sServiceName = "d06010010-service:searchCandRace";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCandRace=dsOutCandRace ";

    fcd_AddParam("RACER_PERIO_NO", cbPerioNo  .Value);
    fcd_AddParam("DAY_NO"        , mskDayNo   .Value);
    fcd_AddParam("DT"            , calRaceDate.Value);

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);

	return true;
}

//조회버튼
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "d06010011-service:searchCandRaceDetl";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCand=dsOutCand "
                     + "dsOutStnd=dsOutStnd "
                     + "dsCfm=dsCfm "
                     + "dsRacePlan=dsRacePlan ";

    for ( var i = 1; i <= toInteger(dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT")); i++ ) {
        for ( var j = 1; j <= 4; j++ ) {
            var sDatasetNm = "dsOutCandRaceDetl_"+i+"R_"+j+"T";
            sOutDataSet += sDatasetNm + "=" + sDatasetNm + " ";
        }
    }

    fcd_AddParam("RACER_PERIO_NO", cbPerioNo  .Value);
    fcd_AddParam("DAY_NO"        , mskDayNo   .Value);
    fcd_AddParam("DT"            , calRaceDate.Value);
    fcd_AddParam("ROUND_CNT"     , dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT"));

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	// if ( !fnc_DatasetChangeCheck(dsOutCandRace ) )
	// {
		// 저장 할 자료가 없음
		// fnc_Message(SNIS_COM_1006, "N");	
		// return false;
	// }

    for ( var i = 1; i <= toInteger(dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT")); i++ ) {
        if ( !fnc_IsMandatory(object("tbMain.tbOrgan.Div0.cboGbn_"     + i +"R"), i + "라운드 구분"  ) ) return false;
        //if ( !fnc_IsMandatory(object("tbMain.tbOrgan.Div0.cboCnt_"     + i +"R"), i + "라운드 주횟수") ) return false;
        if ( !fnc_IsMandatory(object("tbMain.tbOrgan.Div0.mskBoatTime_"+ i +"R"), i + "라운드 시간"  ) ) return false;
    }

	// 필수항목체크
	// oDataSet          = dsOutCandRace;
	// sMandatoryColID   = "DT|ROUND_CNT|PLC_CD|WEATHER|TEMPT|WAT_TEMP|WAT_LEV|WIND_DIRC|WIND_SPD";
	// sMandatoryColName = "출주일자|라운드횟수|장소|날씨|온도|수온|수위|풍향|풍속";
	// oGrid             = grdCandRaceMst;
	// if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    // return false;
	// }

	return true;
}

function fcSave(obj) {

//	if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "d06010011-service:saveCandRaceDetl";
    var sInDataSet   = "dsRacePlan=dsRacePlan:u ";
    var sOutDataSet  = "";

    for ( var i = 1; i <= toInteger(dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT")); i++ ) {
        for ( var j = 1; j <= 4; j++ ) {
            var sDatasetNm = "dsOutCandRaceDetl_"+i+"R_"+j+"T";
            sInDataSet += sDatasetNm + "=" + sDatasetNm + ":u ";
        }
    }
    sOutDataSet = sInDataSet;

    for ( var i = 1; i <= toInteger(dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT")); i++ ) {
        fcd_AddParam("GBN"       + i, object("tbMain.tbOrgan.Div0.cboGbn_"     + i +"R").Value);
        fcd_AddParam("CCIT_CNT"  + i, object("tbMain.tbOrgan.Div0.cboCnt_"     + i +"R").Value);
        fcd_AddParam("BOAT_TIME" + i, object("tbMain.tbOrgan.Div0.mskBoatTime_"+ i +"R").Value);
    }

    fcd_AddParam("RACER_PERIO_NO", cbPerioNo  .Value);
    fcd_AddParam("DAY_NO"        , mskDayNo   .Value);
    fcd_AddParam("DT"            , calRaceDate.Value);
    fcd_AddParam("ROUND_CNT"     , dsOutCandRace.GetColumn(dsOutCandRace.currow, "ROUND_CNT"));
//fnc_datasetPrint(dsOutCandRaceDetl_1R_1T);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

